public edit TransDate editSettlePostingDate(boolean _set, TransDate _settlePostingDate)
{
    if (_set)
    {
        saveDate = _settlePostingDate;
        manager.parmSettlePostingDate(_settlePostingDate);

        custVendSettle_Vend.datePrinciple(manager.parmSettlePostingType());
        custVendSettle_Vend.saveDate(manager.parmSettlePostingDate());
        xSysLastValue::saveLast(custVendSettle_Vend);
    }

    return manager.parmSettlePostingDate();
}

public edit SettleDatePrinc editSettlePostingType(boolean _set, SettleDatePrinc _settlePostingType)
{
    if (_set)
    {
        saveDatePrinciple = _settlePostingType;
        manager.parmSettlePostingType(_settlePostingType);

        if (settlePostingDate.visible() == true && manager.parmSettlePostingType() == SettleDatePrinc::SelectDate)
        {
            settlePostingDate.enabled(true);
        }
        else
        {
            settlePostingDate.enabled(false);
        }

        custVendSettle_Vend.datePrinciple(manager.parmSettlePostingType());
        custVendSettle_Vend.saveDate(manager.parmSettlePostingDate());
        xSysLastValue::saveLast(custVendSettle_Vend);
    }

    return manager.parmSettlePostingType();
}

void existSpecTrans()
{
    if (manager.getSpecTransCount())
    {
        if (SpecTrans::promptRemoveMarkedTransactions())
        {
            manager.resetMarkedTrans();
        }
        else
        {
            settlementFilterDebitType  = SettlementFilterType_RU::None;
            settlementFilterCreditType = SettlementFilterType_RU::None;
        }
    }

    filterDebitType.selection(settlementFilterDebitType);
    element.setFilterFieldAppearance(settlementFilterDebitType, filterDebitValue, companyDebit);

    filterCreditType.selection(settlementFilterCreditType);
    element.setFilterFieldAppearance(settlementFilterCreditType, filterCreditValue, companyCredit);

    this.visibleCreditInvoiceAmountForTrans();
    this.visibleDebitInvoiceAmountForTrans();
}

void findCashDisc(VendTransOpen _vendTransOpen, FormDataSource  _vendTransCashDisc_ds)
{
    VendTransCashDisc   localVendTransCashDisc;
    VendTransCashDisc   vendTransCashDisc;

    vendTransCashDisc = _vendTransCashDisc_ds.cursor();

    //find the best possible cash disc for the actual date
    switch (_vendTransOpen.UseCashDisc)
    {
        case UseCashDisc::Normal:
            if (vendTransCashDisc)
            {
                localVendTransCashDisc = _vendTransCashDisc_ds.getFirst();
                while (localVendTransCashDisc.RecId && localVendTransCashDisc.CashDiscdate < num2date(date2num(custVendOpenTransEngine.transactionDate()) - discGraceDays))
                {
                    localVendTransCashDisc = _vendTransCashDisc_ds.getNext();
                }
            }
            if (localVendTransCashDisc.RecId)
            {
                _vendTransCashDisc_ds.positionToRecord(localVendTransCashDisc);
            }
            else
            {
                _vendTransCashDisc_ds.last();
            }
            break;

        case UseCashDisc::Always:
            _vendTransCashDisc_ds.first();
            break;

        case UseCashDisc::Never:
            _vendTransCashDisc_ds.last();
            break;

        default:
    }
}

public void init()
{
    if (!element.args().record())
        throw error(Error::missingRecord(funcName()));

    common              = element.args().record();

    super();

    custVendOpenTransEngine = new CustVendOpenTransEngine_Vend_RU(this); //Create class to keep form's logic

    xSysLastValue::getLast(this);

    switch (common.TableId)
    {
        case (tableNum(VendTable)):
            this.initFormFromVendTable();
            settlePostingTypeDate.visible(true);
            break;

        case (tableNum(PurchTable)):
            this.initFormFromPurchTable();
            settlePostingTypeDate.visible(false);
            break;

        case (tableNum(LedgerJournalTrans)):
            this.initFormFromLedgerJournalTrans();
            settlePostingTypeDate.visible(false);
            break;

        default :
    }

    element.createCustVendOpenTransManager();

    element.existSpecTrans();

    //Datasources initialize
    custVendOpenTransEngine.setQueryForDebitsDS(settlementFilterDebitType);     //Set new query into datasources for debit
    custVendOpenTransEngine.setQueryForCreditsDS(settlementFilterCreditType);    //Set new query into datasources for credit

    //Image set
    imageList = new ImageListAppl_CustVend();
    isMarkedDebit.imageList(imageList.imageList());
    isMarkedCredit.imageList(imageList.imageList());

    companyDebit.text(curext());
    companyCredit.text(curext());

    //Text change in labels
    showRemainAmountCur.label(strFmt("@SYS62022", custVendOpenTransEngine.currencyCode()));

    custVendSettle_Vend = this.createCustVendSettle();
    saveDatePrinciple   = custVendSettle_Vend.datePrinciple();
    if (saveDatePrinciple == SettleDatePrinc::SelectDate)
    {
        saveDate          = custVendSettle_Vend.saveDate();
    }
    else if (saveDatePrinciple == SettleDatePrinc::DaysDate)
    {
        saveDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    }

    manager.parmSettlePostingType(saveDatePrinciple);
    manager.parmSettlePostingDate(saveDate);

    if (settlePostingDate.visible() == true && manager.parmSettlePostingType() == SettleDatePrinc::SelectDate)
    {
        settlePostingDate.enabled(true);
    }
    else
    {
        settlePostingDate.enabled(false);
    }

    this.updateDataSourceStatic();
    this.updateDataSourceDynamic();

    this.initRemainAmount();    //Initialize value into remainAmountXXx

    debitVendTrans_DS.object (fieldNum(VendTrans, VendPaymentGroup)).visible(custVendOpenTransEngine.paymentGroupEnabled());
    creditVendTrans_DS.object(fieldNum(VendTrans, VendPaymentGroup)).visible(custVendOpenTransEngine.paymentGroupEnabled());
}

