//It do initialization form from VendTable
void initFormFromVendTable()
{
    if (SpecTrans::existMarkedPayment(custVendOpenTransEngine.custVendTable().DataAreaId,
                                      custVendOpenTransEngine.custVendTable().TableId,
                                      custVendOpenTransEngine.custVendTable().RecId))
    {
        debitVendTransOpen_SettleExchRate.allowEdit(true);
        creditVendTransOpen_SettleExchRate.allowEdit(true);
    }
    else
    {
        debitVendTransOpen_SettleExchRate.allowEdit(false);
        creditVendTransOpen_SettleExchRate.allowEdit(false);
    }
}

