public boolean unpack(container packedClass)
{
    Integer     version     = conPeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            break;
        default:
            return false;
    }

    return true;
}

public void updateDataSourceDynamic()
{
    container   companies;
    int         i;

    void setCompanyRange(Query _query)
    {
        _query.clearCompanyRange();

        for (i = 1; i <= conLen(companies); i++)
        {
            _query.addCompanyRange(conPeek(companies, i));
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        companies = manager.parmSharedServiceCompanies();

        setCompanyRange(debitVendTransOpen_ds.query());
        setCompanyRange(creditVendTransOpen_ds.query());

        qbrPartyDebit.value(queryValue(manager.getParty()));
        qbrPartyCredit.value(queryValue(manager.getParty()));
    }
}

public void updateDataSourceStatic()
{
    QueryBuildDataSource    qbdsVendTransOpen;
    QueryBuildDataSource    qbdsVendTrans;
    QueryBuildDataSource    qbdsVendTable;

    QueryBuildRange getPartyRange(Query _query)
    {
        qbdsVendTransOpen = _query.dataSourceTable(tableNum(VendTransOpen));
        qbdsVendTransOpen.clearDynalinks();

        qbdsVendTrans = _query.dataSourceTable(tableNum(VendTrans));
        qbdsVendTable = qbdsVendTrans.addDataSource(tableNum(VendTable));

        qbdsVendTable.relations(true);
        qbdsVendTable.joinMode(JoinMode::InnerJoin);

        qbdsVendTable.fetchMode(0);

        qbdsVendTable.addSelectionField(fieldNum(VendTable, Party));

        return qbdsVendTable.addRange(fieldNum(VendTable, Party));
    }
    ;

    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        qbrPartyDebit  = getPartyRange(debitVendTransOpen_ds.query());
        qbrPartyCredit = getPartyRange(creditVendTransOpen_ds.query());
    }
}

void updateParty()
{
    CustVendOpenTransManager managerLocal;

    common = element.args().record();

    managerLocal = CustVendOpenTransManager::construct(common);

    if (managerLocal.getParty() != manager.getParty())
    {
        manager = managerLocal;
        element.updateDataSourceDynamic();

        element.existSpecTrans();

        custVendOpenTransEngine.linkActive(settlementFilterDebitType, settlementFilterCreditType);

        element.refreshRemainAmounts();
    }
}

