public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup          = SysTableLookup::newParameters(tableNum(VendPaymentGroupLookup),_formControl);
    Query                   query                   = new Query();
    QueryBuildDataSource    queryBuildDataSource    = query.addDataSource(tableNum(VendPaymentGroupLookup));

    sysTableLookup.addLookupfield(fieldNum(VendPaymentGroupLookup, VendPaymentGroup));

    queryBuildDataSource.addRange(fieldNum(VendPaymentGroupLookup, AccountNum)).value(debitVendTrans.AccountNum);

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();

    super(_formControl, _filterStr);
}

public void modified()
{
    VendPaymentGrouping origPaymentGroup = debitVendTrans.orig().VendPaymentGroup;

    super();

    if (debitVendTrans.validateWrite())
    {
        debitVendTrans.write();
    }

    debitVendTrans_ds.executeQuery();
    debitVendTrans_ds.reread();
    debitVendTrans_ds.refresh();

    //if a payment group is deleted, and the settlement is currently marked, and there are other records in the payment group, then unmark this record.
    if (debitVendTrans.VendPaymentGroup == '' && debitVendTransOpen_ds.markTrans(false, debitVendTransOpen, NoYes::No)
            && SpecTrans::findNumberOfMarkedVendPaymentGroups(origPaymentGroup, debitVendTrans.company()) > 0)
    {
        debitVendTransOpen_ds.markTrans(true, debitVendTransOpen, NoYes::No);
    }
    //if the settlement is marked and payment group is changed to another payment group, then mark the entire group.
    else if (debitVendTrans.VendPaymentGroup != '' && debitVendTransOpen_ds.markTrans(false, debitVendTransOpen, NoYes::No))
    {
        debitVendTransOpen_ds.markTrans(true, debitVendTransOpen, NoYes::Yes);
    }
    //if the payment group is changed to a marked payment group, mark that payment group.
    else if (SpecTrans::findNumberOfMarkedVendPaymentGroups(debitVendTrans.VendPaymentGroup, debitVendTrans.company()) > 0)
    {
        debitVendTransOpen_ds.markTrans(true, debitVendTransOpen, NoYes::Yes);
    }
}

