    if (creditVendTrans.VendPaymentGroup == '' && creditVendTransOpen_ds.markTrans(false, creditVendTransOpen, NoYes::No)
            && SpecTrans::findNumberOfMarkedVendPaymentGroups(origPaymentGroup, creditVendTrans.company()) > 0)
    {
        creditVendTransOpen_ds.markTrans(true, creditVendTransOpen, NoYes::No);
    }
    //if the settlement is marked and payment group is changed to another payment group, then mark the entire group.
    else if (creditVendTrans.VendPaymentGroup != '' && creditVendTransOpen_ds.markTrans(false, creditVendTransOpen, NoYes::No))
    {
        creditVendTransOpen_ds.markTrans(true, creditVendTransOpen, NoYes::Yes);
    }
    //if the payment group is changed to a marked payment group, mark that payment group.
    else if (SpecTrans::findNumberOfMarkedVendPaymentGroups(creditVendTrans.VendPaymentGroup, creditVendTrans.company()) > 0)
    {
        creditVendTransOpen_ds.markTrans(true, creditVendTransOpen, NoYes::Yes);
