public void init()
{
    QueryBuildDataSource    ds;
    QueryBuildRange         range;

    super();

    ds = this.query().dataSourceTable(tableNum(VendTransOpen));

    range = findOrCreateRange_W(ds, fieldNum(VendTransOpen, AmountCur));
    range.status(RangeStatus::Locked);
    range.value(strFmt('((%1 >= 0) || (%2 >= 0))', fieldStr(VendTransOpen, AmountCur), fieldStr(VendTransOpen, AmountMST)));
}

display Amount invoiceAmountForTrans(VendTransOpen _vendTransOpen)
{
    Amount      amount;

    if (_vendTransOpen.RecId)
    {
        amount  = custVendOpenTransEngine.invoiceAmountForTrans(_vendTransOpen.RefRecId, filterDebitValue.text());
    }
    amount = abs(amount);

    //Correct sign returned value
    if ((debitVendTrans.AmountMST < 0) || (debitVendTrans.AmountCur < 0))
        amount = -amount;

    return amount;
}

