public void write()
{
    VendTransCashDisc   vendTransCashDiscLocal;

    super();

    if (!debitVendTransOpen.PossibleCashDisc)
    {
        vendTransCashDiscLocal = VendTransCashDisc::findRef(debitVendTransOpen.TableId, debitVendTransOpen.RecId);
        if (vendTransCashDiscLocal.RecId)
        {
            debitVendTransOpen.PossibleCashDisc = vendTransCashDiscLocal.CashDiscAmount;
            debitVendTransOpen.CashDiscDate     = vendTransCashDiscLocal.CashDiscdate;
            debitVendTransOpen.update();
        }
    }
}

void linkActive()
{
    super();

    element.findCashDisc(creditVendTransOpen, this);
}

display AmountCur remainingAmount(VendTransCashDisc _vendTransCashDisc)
{
    if (_vendTransCashDisc.CashDiscdate)
    {
        return creditVendTransOpen.remainAmountCashDisc(_vendTransCashDisc.CashDiscdate);
    }
    else
    {
        return creditVendTransOpen.AmountCur;
    }
}

public void write()
{
    VendTransCashDisc   vendTransCashDiscLocal;

    super();

    if (!creditVendTransOpen.PossibleCashDisc)
    {
        vendTransCashDiscLocal = VendTransCashDisc::findRef(creditVendTransOpen.TableId, creditVendTransOpen.RecId);
        if (vendTransCashDiscLocal.RecId)
        {
            creditVendTransOpen.PossibleCashDisc = vendTransCashDiscLocal.CashDiscAmount;
            creditVendTransOpen.CashDiscDate     = vendTransCashDiscLocal.CashDiscdate;
            creditVendTransOpen.update();
        }
    }
}

