void clicked()
{
    super();

    if (! manager.validateSettlePostingDate(saveDatePrinciple, saveDate))
    {
        return;
    }

    if (VendTrans::settleTransact(custVendOpenTransEngine.custVendTable(), null, true, saveDatePrinciple, saveDate, DimSettlementType_RU::Manual))
    {
        custVendOpenTransEngine.updateNow();
        element.transResearch();   //Research datasources
    }
}

public boolean modified()
{
    settlementFilterDebitType = this.selection();

    if (settlementFilterDebitType != SettlementFilterType_RU::Invoice)
    {
        element.visibleDebitInvoiceAmountForTrans();
    }

    element.setFilterFieldAppearance(settlementFilterDebitType, filterDebitValue, companyDebit);

    custVendOpenTransEngine.setQueryForDebitsDS(settlementFilterDebitType);

    element.refreshRemainAmounts();

    if (settlementFilterDebitType == SettlementFilterType_RU::Invoice)
    {
        element.visibleDebitInvoiceAmountForTrans();
    }

    return true;
}

public void lookup()
{
    custVendOpenTransEngine.filterLookup(settlementFilterDebitType, this, companyDebit.text());
}

public boolean modified()
{
    custVendOpenTransEngine.setFilterValue(
        settlementFilterDebitType,
        debitVendTransOpen_ds,
        filterDebitValue.text());
    element.refreshRemainAmounts();

    return true;
}

