/// <summary>
/// The method calculate total debt
/// </summary>
/// <param name="_query">
/// The current query from datasource
/// </param>
public void calcTotalsDebt(Query       _query)
{
    VendOverdueCITPITDebtJournalLine_W  totalDebtLines;
    Query                               query = new Query(_query);
    QueryRun                            queryRun;
    QueryBuildDataSource                qbds;

    qbds        = query.dataSourceTable(tableNum(VendOverdueCITPITDebtJournalLine_W));
    qbds.addSelectionField(fieldNum(VendOverdueCITPITDebtJournalLine_W, OverdueAmountMST), SelectionField::Sum);

    query.clearGroupBy();

    queryRun    = new QueryRun(query);
    queryRun.next();

    totalDebtLines  = queryRun.get(tableNum(VendOverdueCITPITDebtJournalLine_W));
    totalDebtOverdueAmountMST       = - totalDebtLines.OverdueAmountMST;
}

