public Query initQuery(Query    _query)
{
    Query                       query   = new Query(_query);
    QueryBuildDataSource        qbdsDebt, qbdsTrans, qbdsTable, qbdsDirParty;
    QueryBuildFieldList         qbdsFL;

    qbdsDebt        = query.dataSourceTable(tableNum(VendOverdueCITPITDebtJournalLine_W));
    qbdsTrans       = query.dataSourceTable(tableNum(VendTrans));
    qbdsTable       = query.dataSourceTable(tableNum(VendTable));
    qbdsDirParty    = query.dataSourceTable(tableNum(DirPartyTable));

    if (summary.value())
    {
        qbdsDebt.addSelectionField(fieldNum(VendOverdueCITPITDebtJournalLine_W, DueDate), SelectionField::Max);
        qbdsDebt.addSelectionField(fieldNum(VendOverdueCITPITDebtJournalLine_W, OverdueAmountCur), SelectionField::Sum);
        qbdsDebt.addSelectionField(fieldNum(VendOverdueCITPITDebtJournalLine_W, OverdueAmountMST), SelectionField::Sum);

        qbdsDebt.addGroupByField(fieldNum(VendOverdueCITPITDebtJournalLine_W, JournalId));
        qbdsDebt.addGroupByField(fieldNum(VendOverdueCITPITDebtJournalLine_W, TransRefRecId));

        qbdsTrans.addGroupByField(fieldNum(VendTrans, Invoice));
        qbdsTrans.addGroupByField(fieldNum(VendTrans, AmountCur));
        qbdsTrans.addGroupByField(fieldNum(VendTrans, AmountMST));
        qbdsTrans.addGroupByField(fieldNum(VendTrans, TransDate));

        qbdsTable.addGroupByField(fieldNum(VendTable, AccountNum));
        qbdsTable.addGroupByField(fieldNum(VendTable, vatNum));
        qbdsTable.addGroupByField(fieldNum(VendTable, Party));

        qbdsDirParty.addGroupByField(fieldNum(DirPartyTable, Name));
    }
    else
    {
        query.clearGroupBy();
        qbdsFL      = qbdsDebt.fields();
        qbdsFL.clearFieldList();
    }

    return query;
}

