}

private void refreshFormPart(str partName)
{
    PartList partList = new PartList(element);

    // For each part in the form
    for (int i = 1; i <= partList.partCount(); i++)
    {
        FormPartControl partControl = partList.getPartControlById(i);

        if (partControl && partControl.formName() == partName)
        {
            partControl.refresh();
            break;
        }
    }
}

