    this.query().dataSourceTable(tableNum(VendOverdueVATJournalTable_W)).addSortField(fieldNum(VendOverdueVATJournalTable_W, TransDate));
    this.query().dataSourceTable(tableNum(VendOverdueVATJournalTable_W)).addSortField(fieldNum(VendOverdueVATJournalTable_W, JournalId));
}

