void enableFieldsActive_IN()
{
    boolean isTaxesMarked = TaxParameters::checkTaxParameters_IN();

    if (!TaxParameters::isCustomsEnable_IN())
    {
        vendParameters_ds.object(fieldNum(VendParameters, CustomsLandingChargesPct_IN)).enabled(false);
    }
    else
    {
        vendParameters_ds.object(fieldNum(VendParameters, CustomsLandingChargesPct_IN)).enabled(true);
    }

    purchParameters_ds.object(fieldNum(PurchParameters, SplitDeliveryInvoice_IN)).visible(isTaxesMarked);
    purchParameters_ds.object(fieldNum(PurchParameters, SplitDeliveryPackingSlip_IN)).visible(isTaxesMarked);
}

