void numberSeqPreInit()
{
    runExecuteDirect = false;

    numberSequenceModules = [NumberSeqModule::Vend];

    numberSeqApplicationModule = new NumberSeqModuleVendor();
    // <GEERU><GEEU>
    if (enableAdvanceHolders)
    {
        numberSequenceModules += NumberSeqModule::Empl_RU;
        numberSeqApplicationModuleEmpl = new NumberSeqModuleEmpl_RU();
    }
    // </GEERU></GEEU>
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

public void setupExtendedPriceMatchingOptions(UseExtendedPriceMatching _useExtendedPriceMatching)
{
    switch (_useExtendedPriceMatching)
    {
        case ExtendedPriceMatching::None:
            vendParameters.UseExtendedPriceMatching = ExtendedPriceMatching::None;
            vendParameters_ExtendedPriceTolerancePercentage.enabled(false);
            vendParameters_ExtendedPriceToleranceAmount.enabled(false);
            vendParameters_DisplayExtendedPriceToleranceIcon.enabled(false);
            VendParameters_ExtendedPriceAmountCurrencyType.enabled(false);
            break;

        case ExtendedPriceMatching::Amount:
            vendParameters_ExtendedPriceTolerancePercentage.enabled(false);
            vendParameters_ExtendedPriceToleranceAmount.enabled(true);
            vendParameters_DisplayExtendedPriceToleranceIcon.enabled(true);
            VendParameters_ExtendedPriceAmountCurrencyType.enabled(true);
            break;

        case ExtendedPriceMatching::Percentage:
            vendParameters_ExtendedPriceTolerancePercentage.enabled(true);
            vendParameters_ExtendedPriceToleranceAmount.enabled(false);
            vendParameters_DisplayExtendedPriceToleranceIcon.enabled(true);
            VendParameters_ExtendedPriceAmountCurrencyType.enabled(false);
            break;

        case ExtendedPriceMatching::PercentAndAmount:
            vendParameters_ExtendedPriceTolerancePercentage.enabled(true);
            vendParameters_ExtendedPriceToleranceAmount.enabled(true);
            vendParameters_DisplayExtendedPriceToleranceIcon.enabled(true);
            VendParameters_ExtendedPriceAmountCurrencyType.enabled(true);
            break;
    }
}

/// <summary>
/// Enables and disables miscellaneous charges matching fields.
/// </summary>
/// <param name="_useInvoiceMiscChargeMatching">
/// NoYes enumeration used to enable and disable misc. charges matching fields.
/// </param>
/// <remarks>
/// Misc. charges matching is only enabled when Markup and PurchInvoiceMatching configuration keys are both enabled.
/// </remarks>
public void setupMiscChargeMatchingOptions(UseInvoiceMiscChargeMatching _useInvoiceMiscChargeMatching)
{
    boolean isMarkupConfigurationEnabled;

    isMarkupConfigurationEnabled = isConfigurationkeyEnabled(configurationKeyNum(Markup));

    if (!isMarkupConfigurationEnabled)
    {
        _useInvoiceMiscChargeMatching = NoYes::No;
    }

    switch (_useInvoiceMiscChargeMatching)
    {
        case NoYes::Yes:
            vendParameters_DisplayMiscChargeToleranceIcon.enabled(true);
            vendParameters_MiscChargeTolerancePercentage.enabled(true);
            break;

        case NoYes::No:
            vendParameters.UseMiscChargeMatching = NoYes::No;
            vendParameters_DisplayMiscChargeToleranceIcon.enabled(false);
            vendParameters_MiscChargeTolerancePercentage.enabled(false);

            if (!isMarkupConfigurationEnabled)
            {
                miscChargeMatching.visible(false);
                vendParameters_DisplayMiscChargeToleranceIcon.visible(false);
                vendParameters_MiscChargeTolerancePercentage.visible(false);
            }
            break;
    }
}

