/// <summary>
/// Validates the value in the internal Invoice Grouping default group field.
/// </summary>
/// <returns>
/// true if validation passes; otherwise, false.
/// </returns>
public boolean validateVendInvoiceInternalGroup()
{
    if (invoiceGrouping_VendInvoiceInternalDefaultGroup.allowEdit() && invoiceGrouping_VendInvoiceInternalDefaultGroup.text() == "")
    {
        return checkFailed(strFmt("@SYS84753", "@SYS130646"));
    }
    else
    {
        return true;
    }
}

/// <summary>
/// Enables or disables the editing of invoice grouping fields based upon current settings in VendParameters.
/// </summary>
/// <param name = "_calledFromInit">
/// true if the method is being called in init; otherwise, false.
/// </param>
/// <remarks>
/// If the boolean _calledFromInit is true then the values should be retrieved from the VendParameters table, not the VendParameters form.
/// If false then the values will be retrieved from the VendParameters form rather than the VendParameters table.
/// </remarks>
public void vendInvoiceGroupingSetEditable(boolean _calledFromInit = false)
{
    if (_calledFromInit)
    {
        invoiceGrouping_VendInvoiceInternalDefaultGroup.allowEdit(VendParameters::find().VendInvoiceDefaultGroupType == VendInvoiceDefaultGroupType::Custom && VendParameters::find().VendInvoiceGroupingEnable == NoYes::Yes);
        invoiceGrouping_VendInvoiceWebServiceDefaultGroup.allowEdit(VendParameters::find().VendInvoiceGroupingEnable == NoYes::Yes);
        invoiceGrouping_VendInvoiceDefaultGroupType.allowEdit(VendParameters::find().VendInvoiceGroupingEnable == NoYes::Yes);
    }
    else
    {
        invoiceGrouping_VendInvoiceInternalDefaultGroup.allowEdit(vendparameters.VendInvoiceDefaultGroupType == VendInvoiceDefaultGroupType::Custom && vendParameters.VendInvoiceGroupingEnable == NoYes::Yes);
        invoiceGrouping_VendInvoiceWebServiceDefaultGroup.allowEdit(vendParameters.VendInvoiceGroupingEnable == NoYes::Yes);
        invoiceGrouping_VendInvoiceDefaultGroupType.allowEdit(vendParameters.VendInvoiceGroupingEnable == NoYes::Yes);
    }
}

