/// <summary>
///     Checks or unchecks all checkboxes for all Financial dimensions.
/// </summary>
/// <param name="_checkAll">
///     True if all checkboxes are to be checked; false, otherwise
/// </param>
private void modifyAllFinancialDimensionMatching(boolean _checkAll = false)
{
    if (_checkAll == true)
    {
        DimensionAttribute allDimensionAttribute;

        while select allDimensionAttribute 
            where allDimensionAttribute.Type == DimensionAttributeType::CustomList
                || allDimensionAttribute.Type == DimensionAttributeType::ExistingList
                || allDimensionAttribute.Type == DimensionAttributeType::MainAccount
            notexists join DimensionAttributeInvoicePurchaseOrderMatching_PSN where DimensionAttributeInvoicePurchaseOrderMatching_PSN.DimensionAttribute == allDimensionAttribute.RecId
        {
            if (!DimensionAttributeInvoicePurchaseOrderMatching_PSN)
            {
                DimensionAttributeInvoicePurchaseOrderMatching_PSN.DimensionAttribute = allDimensionAttribute.RecId;
                DimensionAttributeInvoicePurchaseOrderMatching_PSN.IsMatchingRequired = NoYes::Yes;
                DimensionAttributeInvoicePurchaseOrderMatching_PSN.insert();
            }

        }
    }

    ttsbegin;

    update_recordset DimensionAttributeInvoicePurchaseOrderMatching_PSN
        setting isMatchingRequired = _checkAll;

    ttscommit;

    AccDistInvoicePurchaseOrderMatchingCache_PSN::clearCache();

    DimensionAttributeInvoicePurchaseOrderMatching_PSN_ds.refresh();
}

