/// <summary>
/// Validates the <c>NumberSequenceReference</c> buffer.
/// </summary>
/// <returns>
///     true if the <c>NumberSequenceReference</c> is valid; otherwise, false
/// </returns>
/// <remarks>
/// Validates that the <c>NumberSequenceTable</c> referenced is not setup for manual in several royalty cases.
/// </remarks>
public boolean validateWrite()
{
    boolean ret;

    NumberSequenceDatatype numberSequenceDatatype;
    NumberSequenceTable numberSequenceTableLocal;
    int edtMCRRoyaltyClaimId = extendedTypeNum(MCRRoyaltyClaimId);
    int edtMCRRoyaltyVendInvoiceId = extendedTypeNum(MCRRoyaltyVendInvoiceId);

    ret = super();

    // get the numbersequence data type of the current NumberSequenceReference on the grid
    numberSequenceDatatype = NumberSequenceDatatype::find(NumberSequenceReference.NumberSequenceDatatype);
    // determine if it is royalty claim, royalty invoice, or royalty vendor invoice
    if (numberSequenceDatatype.DatatypeId == edtMCRRoyaltyClaimId
        || numberSequenceDatatype.DatatypeId == edtMCRRoyaltyVendInvoiceId)
    {
        // find the numberSequenceTable
        numberSequenceTableLocal = NumberSequenceTable::find(NumberSequenceReference.NumberSequenceId);
        // if manual, reject change with an error message
        if (numberSequenceTableLocal.Manual == NoYes::Yes)
        {
            error(strFmt("@SYS131762","@MCR33559"));
            ret = false;
        }
    }
    return ret;
}

void removeFilter()
{
    runExecuteDirect = false;
    numbersequenceReference_ds.executeQuery();
}

void executeQuery()
{
    if (runExecuteDirect)
    {
        super();
    }
    else
    {
        runExecuteDirect = true;
        this.queryRun(NumberSeqReference::buildQueryRunMulti(numberSequenceReference,
                                                             tmpIdRef,
                                                             numberSequenceTable,
                                                             numberSequenceModules,
                                                             scope));
        numbersequenceReference_ds.research();
    }
}

int active()
{
    // <GEEPL>
    NumberSequenceDatatype numberSequenceDatatype;
    // </GEEPL>
    int ret;

    ret = super();

    buttonNumberSequenceGroup.enabled(numberSequenceReference.groupEnabled());

    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoPL ]))
    {
        numberSequenceDatatype = NumberSequenceDatatype::find(numberSequenceReference.NumberSequenceDatatype);

        plNumberSequenceGroupInvent.visible(true);
        plNumberSequenceGroupInvent.enabled(
            numberSequenceDatatype.DatatypeId == extendedTypeNum(PurchInternalPackingSlipId) ||
            numberSequenceDatatype.DatatypeId == extendedTypeNum(PackingSlipId));
    }
    else
    {
        plNumberSequenceGroupInvent.visible(false);
    }
    // </GEEPL>

    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT]))
    {
        TaxBookSectionId.visible(false);
    }

    return ret;
}

public display TaxBookSectionId taxBookSectionId(NumberSequenceReference _numberSequenceReference)
{
    NumberSequenceTable numberSequenceTableLocal;

    if (numberSequenceReference.NumberSequenceId)
    {
        select firstonly NumberSequence from numberSequenceTableLocal where numberSequenceTableLocal.RecId == _numberSequenceReference.NumberSequenceId;
    }

    return TaxBookSection::findVoucherSeries(numberSequenceTableLocal.RecId).TaxBookSectionId;
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{

    // Do not call super as we're providing our own disambiguation logic.
    // resolvedRecord = super(_formReferenceControl);
    // Account number field allows number sequence with a scope to current company as well as a global number sequence,
    // the other fields allows number sequence with a scope to current company only.

    NumberSequenceCode code = _formReferenceControl.filterValue(
        AbsoluteFieldBinding::construct(fieldStr(NumberSequenceTable, NumberSequence),
        tableStr(NumberSequenceTable))).value();

    NumberSequenceTable resolvedRecord = NumberSequenceTable::findByNaturalKey(code, scope.getId(true));

    if (!resolvedRecord && ExtendedTypeNum(VendAccount) == NumberSequenceDatatype::find(NumberSequenceReference.NumberSequenceDatatype).DatatypeId)
    {
        NumberSeqScope globalScope = NumberSeqScopeFactory::createGlobalScope();
        resolvedRecord = NumberSequenceTable::findByNaturalKey(code, globalScope.getId(true));
    }

    return resolvedRecord;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    NumberSequenceTable selectedRecord;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tablenum(NumberSequenceTable), _formReferenceControl, true);
    Query lookupQuery;

    // Do not call super as we're providing our own custom lookup logic.
    // selectedRecord = super(_formReferenceControl);

    // Display the Title and Department fields in the lookup form.
    sysTableLookup.addLookupfield(fieldnum(NumberSequenceTable, NumberSequence));

    // Create a custom Query that filters on NumberSequenceScope.
    // Account number field allows global number sequence and number sequence for current company,
    // other fields allow number sequence for current company only.
    lookupQuery = new Query();

    QueryBuildDataSource queryBuildDS = lookupQuery.addDataSource(tablenum(NumberSequenceTable));

    queryBuildDS.addRange(fieldnum(NumberSequenceTable, NumberSequenceScope)).value(queryValue(scope.getId(true)));

    // Allow global number sequence for account number
    if (ExtendedTypeNum(VendAccount) == NumberSequenceDatatype::find(NumberSequenceReference.NumberSequenceDatatype).DatatypeId)
    {
        NumberSeqScope globScope =  NumberSeqScopeFactory::createGlobalScope();
        queryBuildDS.addRange(fieldnum(NumberSequenceTable, NumberSequenceScope)).value(queryValue(globScope.getId()));
    }

    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

