public int active()
{
    int ret = super();

    element.initCreditInvoicing();
    // <GEEU>
    element.initDocumentDateForIntracomVAT();
    // </GEEU>
    invoiceMatching_AutomaticHeaderMatching.enabled(vendParameters.editUseInvoiceMatching());
    invoiceMatching_UseTotalPriceMatching.enabled(vendParameters.editUseInvoiceMatching());
    invoiceMatching_DisplayTotalPriceMatchIcon.enabled(vendParameters.editUseInvoiceMatching() && vendParameters.UseTotalPriceMatching);
    invoicematching_TotalPriceTolerancePercentage.enabled(vendParameters.editUseInvoiceMatching() && vendParameters.UseTotalPriceMatching);

    if (FeatureStateProvider::isFeatureEnabled(VendInvoiceTotalsMatchingDetailFeature::instance()))
    {
        element.enableInvoiceValidationFields();
    }
    element.enableSettleRemainder(VendParameters.SettlementWriteOffEnabled);

    element.enableSetupPageAutomationWorkflowTab(VendParameters.AutomationWorkflowSubmission);
    element.showAutomationProductReceiptMatchingTab();
    element.enableSetupPageAutomationProductReceiptMatchingTab(VendParameters.AutomationProductReceiptMatching);
    VendParameters.AutomationProductReceiptMatchingMaxAttempts = VendParameters::defaultAutomationProductReceiptMatchingMaxAttempts();
    element.enableCrossYearDateAdjusted();

    if (TaxIntegrationUtils::isMultipleTaxIdEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        // For current release 10.0.15, always set error, as document split on different tax id is not enabled.
        VendParameters.TaxIdValidation = TaxIdValidation::Error;
    }

    element.enableCORTabPage(NoYes::No);

    AutomationDefaultQuantityForInvoiceLinesCreation.enabled(VendParameters.AutomationCreateInvoiceLines);

    if (FeatureStateProvider::isFeatureEnabled(VendorBankInformationFeature::instance()))
    {
        if (VendParametersRequiredBankIbanSwiftFeature::instance().isEnabled())
        {
            RequireIbanSwiftCheckBox.visible(false);
        }
        else
        {
            RequireIbanSwiftCheckBox.visible(true);

            if (VendParameters.IsIbanSwiftRequired == IbanSwiftRequired::Yes)
            {
                RequireIbanSwiftCheckBox.checked(true);
            }
            else
            {
                RequireIbanSwiftCheckBox.checked(false);
            }
        }
    }

    return ret;
}

