public boolean modified()
{
    boolean ret;

    if (!invoiceGrouping_VendInvoiceGroupingEnable.checked())
    {
        //if the checkbox is being unchecked
        //check if payment groups can be disabled, if they cannot then throw an error.
        if (!VendParameters::validateVendInvoiceGroupsCanBeDisabled())
        {
            warning("@SYS130660");
            ret = false;
        }
        else
        {
            //make the custom code for web services field not mandatory so squigly line doesnt appear
            invoiceGrouping_VendInvoiceInternalDefaultGroup.mandatory(false);
            ret = super();
            //set the editability of fields associated with this feature
            element.vendInvoiceGroupingSetEditable();
        }
    }
    else
    {
        //otherwise it is being checked
        ret = super();
        //set the editability of fields associated with this feature
        element.vendInvoiceGroupingSetEditable();

        //if the drop down is set to custom then set the custom field as mandatory as well.
        if (vendParameters.VendInvoiceDefaultGroupType == VendInvoiceDefaultGroupType::Custom)
        {
            invoiceGrouping_VendInvoiceInternalDefaultGroup.mandatory(true);
        }
    }

    return ret;
}

/// <summary>
/// This event occurs when the 'Use invoice matching' checkbox is checked or unchecked.
/// </summary>
/// <returns>
/// true if the checking or unchecking action succeeds; otherwise, false.
/// </returns>
/// <remarks>
/// Disables the two related fields when the checkbox is unmarked and enables them when marked.
/// </remarks>
public boolean modified()
{
    boolean ret = super();

    invoiceMatching_PostInvoiceMatchDiscrepancies.enabled(vendParameters.editUseInvoiceMatching());
    invoiceMatching_AutomaticHeaderMatching.enabled(vendParameters.editUseInvoiceMatching());

    invoiceMatching_DisplayPriceToleranceIcon.enabled(vendParameters.editUseInvoiceMatching());
    vendParameters_UseExtendedPriceMatching.enabled(vendParameters.editUseInvoiceMatching());
    vendParameters_MatchingPolicy.enabled(vendParameters.editUseInvoiceMatching());
    vendParameters_MatchingPolicyAllowOverride.enabled(vendParameters.editUseInvoiceMatching());
    invoiceMatching_UseTotalPriceMatching.enabled(vendParameters.editUseInvoiceMatching());
    invoiceMatching_DisplayTotalPriceMatchIcon.enabled(vendParameters.editUseInvoiceMatching() && vendParameters.UseTotalPriceMatching);
    invoiceMatching_TotalPriceTolerancePercentage.enabled(vendParameters.editUseInvoiceMatching() && vendParameters.UseTotalPriceMatching);

    if (FeatureStateProvider::isFeatureEnabled(VendInvoiceTotalsMatchingDetailFeature::instance()))
    {
        element.enableInvoiceValidationFields();
    }

    if (vendParameters.editUseInvoiceMatching())
    {
        element.setupExtendedPriceMatchingOptions(vendParameters.UseExtendedPriceMatching);
    }
    else
    {
        element.setupExtendedPriceMatchingOptions(ExtendedPriceMatching::None);
        element.setupMiscChargeMatchingOptions(NoYes::No);
        vendParameters.UseTotalPriceMatching = NoYes::No;

        // When "Enable invoice matching validation" is not selected in Invoice validation tab,
        // then reset fields in Vendor invoice automation tab.
        vendParameters.AutomationMatchingPriorToWorkflow = NoYes::No;
        vendParameters.AutomationProductReceiptMatching = NoYes::No;
    }

    vendParameters_UseMiscChargeMatching.enabled(vendParameters.editUseInvoiceMatching());

    // "Match product receipts automatically" fasttab config in setup page options for vendor invoice automation feature
    // based on "Enable invoice matching validation" option in Invoice validation tab.
    element.showAutomationProductReceiptMatchingTab();
    AutomationMatchingPriorToWorkflow.enabled(AutomationWorkflowSubmission.checked() && vendParameters.editUseInvoiceMatching());

    return ret;
}

