public boolean validate()
{
    boolean ret = super();

    // if the customer account number sequence been modified to a number sequence with different scope, display warning message.
    if (ret && ExtendedTypeNum(VendAccount) == NumberSequenceDatatype::find(NumberSequenceReference.orig().NumberSequenceDatatype).DatatypeId)
    {
        RefRecId oldNumSeq = NumberSequenceReference.orig().NumberSequenceId;
        RefRecId newNumSeq = NumberSequenceReference_NumberSequenceId.value();

        if (NumberSequenceTable::find(oldNumSeq).NumberSequenceScope != NumberSequenceTable::find(newNumSeq).NumberSequenceScope)
        {
            info("@AccountsPayable:MessageUpdateNumSeqForVendAccount");
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    // When "Automatically submit imported invoices to workflow" is selected
    // and an active vendor invoice workflow configuration exists then
    // enabled all the automation workflow fast tab options,
    // Otherwise they are disabled.
    element.enableSetupPageAutomationWorkflowTab(AutomationWorkflowSubmission.checked() && VendParameters::validateActiveVendInvoiceWorkflowPerCompany());

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    // When the user has selected the "Match product receipts to invoice lines" option,
    // enables all fields in "Match product receipts automatically" fast tab.
    if (AutomationMatchingPriorToWorkflow.checked())
    {
        element.enableSetupPageAutomationProductReceiptMatchingTab(NoYes::Yes);
    }
    return ret;
}

public boolean modified()
{
    boolean ret = super();

    // When the user has selected the "Automatically match product receipt to invoice lines" option,
    // enables all fields in "Match product receipts automatically" fast tab and vice versa.
    if (AutomationProductReceiptMatching.checked() == NoYes::Yes)
    {
        element.enableSetupPageAutomationProductReceiptMatchingTab(NoYes::Yes);
    }
    else if (AutomationProductReceiptMatching.checked() == NoYes::No && AutomationMatchingPriorToWorkflow.checked() == NoYes::Yes)
    {
        element.enableSetupPageAutomationProductReceiptMatchingTab(NoYes::Yes);
    }
    else if (AutomationProductReceiptMatching.checked() == NoYes::No && AutomationMatchingPriorToWorkflow.checked() == NoYes::No)
    {
        element.enableSetupPageAutomationProductReceiptMatchingTab(NoYes::No);
    }

    return ret;
}

/// <summary>
/// Checks alls selections of the Vendor Invoice Matching Feature.
/// </summary>
[Hookable(false)]
public void clicked()
{
    element.modifyAllFinancialDimensionMatching(true);

    super();
}

/// <summary>
/// Clears all selections of the Vendor Invoice Matching Feature.
/// </summary>
[Hookable(false)]
public void clicked()
{
    element.modifyAllFinancialDimensionMatching(false);

    super();
}

public void clicked()
{
    DimensionAttributeInvoicePurchaseOrderMatching_PSN cursorDimensionAttribute;
    select forupdate firstonly cursorDimensionAttribute where cursorDimensionAttribute.DimensionAttribute == DimensionAttribute_ds.cursor().RecId;

    if (cursorDimensionAttribute.IsMatchingRequired)
    {
        ttsbegin;
        cursorDimensionAttribute.IsMatchingRequired = NoYes::No;
        cursorDimensionAttribute.update();
        ttscommit;

    }
    else
    {
        ttsbegin;
        cursorDimensionAttribute.IsMatchingRequired = NoYes::Yes;
        cursorDimensionAttribute.update();
        ttscommit;
    }

    DimensionAttributeInvoicePurchaseOrderMatching_PSN_ds.reread();
    DimensionAttributeInvoicePurchaseOrderMatching_PSN_ds.refresh();

}
