/// <summary>
/// Initialize the param methods and enable or disable the controls.
/// </summary>
public void init()
{
    AccessRight menuItemAccessRight;

    #ISOCountryRegionCodes

    super();

    paymModeTableForm = VendPaymModeTableForm::construct();

    paymModeTableForm.parmDataObjectBankTransType(vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, BankTransType)));
    paymModeTableForm.parmDataObjectInterCompLedgerDimension(vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, InterCompanyLedgerDimension)));
    paymModeTableForm.parmDataObjectExportOnInvoice(vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, ExportOnInvoice)));
    paymModeTableForm.parmDataObjectAccountType(vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, AccountType)));
    paymModeTableForm.parmCtrlTablePaymValidation(PaymControlTable);
    paymModeTableForm.parmCtrlTablePaymAttributes(PaymAttributeTable);

    paymModeTableForm.formInitPost(vendPaymModeTable);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]))
    {
        norwayEnabled = true;
    }

    if (BankParameters::find().EnablePDC)
    {
        posting_PDCClearingPosting.visible(true);
    }

    allowPaymentCopies = BankParameters::find().AllowPaymentCopies;
    this.updateDesign(allowPaymentCopies);

    Posting_InterCoAccount.parmPostingType(LedgerPostingType::None);

    Posting_PaymAccount.parmIsDefaultAccount(true);

    menuItemAccessRight = FormSecurity::getMenuItemAccessRight(element);

    // "allowEdit" property does not disable ListView checkboxes, set the "enabled" property instead based on access rights
    PaymAttributeTable.enabled(menuItemAccessRight != AccessRight::View);
    PaymControlTable.enabled(menuItemAccessRight != AccessRight::View);
}

