void setFieldAccess()
{
    if (BrazilParameters::isEnabled())
    {
        if (vendPaymModeTable.ClassId == classnum(VendOutPaym_CNAB_BR))
        {
            vendPaymModeTable.PaymentType   = this.getPaymentTypeElectronicPayment();
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, ExportLayoutGroupId_BR)).allowEdit(true);
        }
        else
        {
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, ExportLayoutGroupId_BR)).allowEdit(false);
            vendPaymModeTable.ExportLayoutGroupId_BR = '';
        }

        if (vendPaymModeTable.ClassIdReturn == classnum(VendPaymReconcilImport_EP_Return_BR))
        {
            vendPaymModeTable.PaymentType   = this.getPaymentTypeElectronicPayment();
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, ReturnLayoutGroupId_BR)).allowEdit(true);
        }
        else
        {
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, ReturnLayoutGroupId_BR)).allowEdit(false);
            vendPaymModeTable.ReturnLayoutGroupId_BR = '';
        }

        if (vendPaymModeTable.ClassId == classnum(VendOutPaym_CNAB_BR))
        {
            vendPaymModeTable.PaymOnInvoice     = NoYes::No;
            vendPaymModeTable.ExportOnInvoice   = NoYes::No;
            vendPaymModeTable.PaymJournalNameId = '';

            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, PaymOnInvoice)).enabled(false);
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, ExportOnInvoice)).enabled(false);
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, PaymJournalNameId)).enabled(false);
        }
        else
        {
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, PaymOnInvoice)).enabled(true);
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, ExportOnInvoice)).enabled(true);
            vendPaymModeTable_ds.object(fieldnum(VendPaymModeTable, PaymJournalNameId)).enabled(true);
        }
        vendPaymModeTable_ds.refresh();
    }
}

public void setInvoiceUpdateAccess_BR()
{
    boolean allowEdit = true;

    vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, PaymOnInvoice)).allowEdit(allowEdit);
    vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, ExportOnInvoice)).allowEdit(allowEdit);
    vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, PaymJournalNameId)).allowEdit(allowEdit);

    vendPaymModeTable_ds.refresh();
}

public void setPostingLevelAccess_BR()
{
    if (vendPaymModeTable.PaymSumBy == PaymSumBy::Total)
    {
        vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, Posting_BR)).allowEdit(true);
    }
    else
    {
        vendPaymModeTable_ds.object(fieldNum(VendPaymModeTable, Posting_BR)).allowEdit(false);
        vendPaymModeTable.Posting_BR = Posting_BR::None;
    }
}

