}

    /// <summary>
    /// Enables or disables file format fields.
    /// </summary>
    /// <param name = "_enableExport">If true, field FileFormats_M_actionClassName is enabled and field FileFormats_ERFormatMappingID is disabled accordingly.</param>
    /// <param name = "_enableReturn">If true, field FileFormats_M_actionClassNameReturn is enabled and fields FileFormats_ERReturnConfiguration, FileFormats_ERReturnConfigurationSecondary are disabled.</param>
    void enableFileFormatFields(NoYes _enableExport, NoYes _enableReturn = NoYes::No)
    {
        NoYes enableExport = _enableExport;
        NoYes enableBR = _enableExport || BrazilParameters::isEnabled();

        FileFormats_ERFormatMappingID.enabled(!enableExport);
        FileFormats_NACHAIAT.enabled(!enableExport);
        FileFormats_ExportLayoutGroupId_BR.enabled(enableExport);
	    FileFormats_ReturnLayoutGroupId_BR.enabled(enableBR);

        FileFormats_M_actionClassNameReturn.enabled(_enableReturn);
        FileFormats_ERReturnConfiguration.enabled(!_enableReturn);
        FileFormats_ERReturnConfigurationSecondary.enabled(!_enableReturn);

        if (this.checkPaymentTypeForPromissoryNote())
        {
            enableExport   = NoYes::Yes;
        }

        FileFormats_M_actionClassName.enabled(enableExport);
        FileFormats_M_actionClassNameFileAnalyze.enabled(enableExport);

        BankClient_RU_UseCustomFormat_RU.enabled(!enableExport);
        BankClient_RU_bankClientActionClassName_RU.enabled(!enableExport && VendPaymModeTable.UseCustomFormat_RU);
    }

