public void executeQuery()
{
    BankAccountTable bankAccountTable;

    if (element.args().record() && element.args().caller().name() == formStr(BankAccountTable))
    {
        vendPaymModeTable_ds.query().dataSourceNo(1).clearDynalinks();

        bankAccountTable = element.args().record();
        QueryBuildRange criteriaBankAccountID = vendPaymModeTable_ds.query().dataSourceNo(1).addRange(fieldNum(VendPaymModeTable, PaymentLedgerDimension));
        criteriaBankAccountID.value(queryValue(LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(bankAccountTable.AccountID, LedgerJournalACType::Bank)));
    }
    super();
}

