int active()
{
    int ret;
    boolean localAllowPaymentCopies;

    ret = super();

    paymModeTableForm.datasourceActivePost();
    if (norwayEnabled)
    {
        vendPaymModeTable_ds.enableFields();
    }

    if (allowPaymentCopies && vendPaymModeTable.AccountType == LedgerJournalACType::Bank)
    {
        localAllowPaymentCopies = true;
    }

    element.updateDesign(localAllowPaymentCopies);
    element.enableFields();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.setFieldAccess();

        if (vendPaymModetable.ClassId == classnum(VendOutPaym_CNAB_BR) ||
            vendPaymModetable.ClassIdReturn == classnum(VendPaymReconcilImport_EP_Return_BR))
        {
            if (vendPaymModeTable.PaymSumBy != PaymSumBy::Invoice)
            {
                ret = checkFailed(strfmt("@GLS670",vendPaymModeTable.PaymSumBy,"@SYS25194"));
            }
        }

        element.setPostingLevelAccess_BR();
        element.setInvoiceUpdateAccess_BR();
        this.setDescPrimaryMethodAccess_BR();
    }
    // </GBR>

    PaymentFormatCodes_W.visible(PaymentFormatCodeSets_W::isISO20022Country());

    return ret;
}

