public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(VendPaymentGroupLookup),vendTrans_VendPaymentGroup);
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(VendPaymentGroupLookup));

    sysTableLookup.addLookupfield(fieldNum(VendPaymentGroupLookup, VendPaymentGroup));

    queryBuildDataSource.addRange(fieldNum(VendPaymentGroupLookup, AccountNum)).value(queryValue(vendTrans.AccountNum));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
    super(_formControl, _filterStr);
}

public int active()
{
    int ret;

    ret = super();

    // Discount to take is editable when use cash discount is normal and and partial discounts
    // are allowed, or it use cash discount is always.
    if ((vendTransOpen.UseCashDisc == UseCashDisc::Always)
            || (vendTransOpen.UseCashDisc == UseCashDisc::Normal
                && VendParameters::find().CashDiscForPartialPaym == true))
    {
        specTrans_CashDiscToTake.allowEdit(true);
    }
    else
    {
        specTrans_CashDiscToTake.allowEdit(false);
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        interestAndFine_BR_InterestAmountPaymCur_BR.label(strFmt("@SYS53547", custVendPaymProposalLine.CurrencyCode));
        interestAndFine_BR_FineAmountPaymCur_BR.label(strFmt("@GLS50821", custVendPaymProposalLine.CurrencyCode));
        paymProposalEditForm.adjustInterestFineEdition_BR();
    }
    // </GBR>
    return ret;
}

public void modified()
{
    super();

    paymProposalEditForm.fieldModifiedTrans_DueDatePost();
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        if (vendTransOpen.InterestCode_BR && vendTransOpen.FineCode_BR)
        {
            if ((Box::yesNo(strFmt("@GLS1268",
                                    fieldPName(VendTransOpen, DueDate),
                                    fieldPName(VendTransOpen, InterestAmount_BR),
                                    fieldPName(VendTransOpen, FineAmount_BR)),
                                    DialogButton::No) == DialogButton::Yes))
            {
                paymProposalEditForm.fieldModified_FineCodeDueDate_BR();
                paymProposalEditForm.fieldModified_InterestCodeDueDate_BR();
            }
        }
        else
        {
            if (vendTransOpen.InterestCode_BR)
            {
                if (Box::yesNo(strFmt("@GLS1009",fieldPName(VendTransOpen, DueDate),
                                fieldPName(VendTransOpen, InterestAmount_BR)), DialogButton::No) == DialogButton::Yes)
                {
                    paymProposalEditForm.fieldModified_InterestCodeDueDate_BR();
                }
            }
            else
            {
                if (vendTransOpen.FineCode_BR)
                {
                    if ((Box::yesNo(strFmt("@GLS1009",fieldPName(VendTransOpen, DueDate),
                                    fieldPName(VendTransOpen, FineAmount_BR)), DialogButton::No) == DialogButton::Yes))
                    {
                        paymProposalEditForm.fieldModified_FineCodeDueDate_BR();
                    }
                }
            }
        }
    }
    // </GBR>
}

