        vendTransCashDisc_ds.allowEdit(false);
    }

    return ret;
}

public display AmountCur remainingAmount(VendTransCashDisc _vendTransCashDisc)
{
    VendTransOpenData vendTransOpenData = vendTransOpen.transOpenData();

    return vendTransOpenData.remainAmountCashDisc(
        _vendTransCashDisc.CashDiscdate,
        0,
        true,
        specTrans.Balance01,
        specTrans.CashDiscToTake);
}

