public void init()
{
    super();

    if (VendPostingFormDisableCancelToggleFlight::instance().isEnabled())
    {
        Rights_group_Cancel.visible(false);
    }
}

public void openInExcel()
{
    DocuTemplateName templateName = resourceStr(VendorPostingProfileTemplate);
    str lineEntityName = tableStr(VendorPostingProfileLineEntity);
    str lineEntityPostingProfile = fieldStr(VendorPostingProfileLineEntity, PostingProfile);
    str lineEntityDataAreaId = fieldStr(VendorPostingProfileLineEntity, dataAreaId);
    str headerEntityName = tableStr(VendorPostingProfileHeaderEntity);
    str headerEntityPostingProfile = fieldStr(VendorPostingProfileHeaderEntity, PostingProfile);
    str headerEntityDataAreaId = fieldStr(VendorPostingProfileHeaderEntity, dataAreaId);

    DocuTemplate template = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, templateName);

    // Ensure the template was present
    if (template && template.TemplateID == templateName)
    {
        Map filtersToApply = new Map(Types::String, Types::Class);

        // Create lines filter
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);

        var filter = filterBuilder.and(
                filterBuilder.areEqual(lineEntityPostingProfile, VendLedger.PostingProfile),
                filterBuilder.areEqual(lineEntityDataAreaId, VendLedger.DataAreaId));

        filtersToApply.insert(lineEntityName, filter);

        // Create header filter
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);

        filter = filterBuilder.and(
                lineFilterBuilder.areEqual(headerEntityPostingProfile, VendLedger.PostingProfile),
                lineFilterBuilder.areEqual(headerEntityDataAreaId, VendLedger.DataAreaId));

        filtersToApply.insert(headerEntityName, filter);

        // Generate the workbook using the template and filters
        DocuTemplateRender renderer = new DocuTemplateRender();
        str documentUrl = renderer.renderTemplateToStorage(template, filtersToApply, OfficeTrimmable::construct());

        // Pass the workbook to the user
        if (documentUrl)
        {
            Browser b = new Browser();
            b.navigate(documentUrl, false, false);
        }
        else
        {
            error(strFmt("@ApplicationFoundation:DocuTemplateGenerationFailed", templateName));
        }
    }
    else
    {
        warning(strFmt("@ApplicationFoundation:DocuTemplateNotFound", templateName));
    }
}

void setLookupButton()
{
    VendLedgerAccounts_NumHeader.allowEdit(true);
    VendLedgerAccounts_NumHeader.skip(false);
    VendLedgerAccounts_NumHeader.lookupButton(0);

    if (vendLedgerAccounts.AccountCode == TableGroupAll::All)
    {
        VendLedgerAccounts_NumHeader.allowEdit(false);
        VendLedgerAccounts_NumHeader.skip(true);
        VendLedgerAccounts_NumHeader.lookupButton(1);
        vendLedgerAccounts.Num = '';
    }
}

int active()
{
    int ret = super();
    element.setLookupButton();
    return ret;
}

