[Form]
public class VendProspectiveVendorRegistrationWizard extends FormRun
{
    private container                                   questionnaires;
    private int                                         currentQuestionnairePos = 1;
    private VendProspectiveVendorRegistrationWizard     prospectiveVendorRegistrationWizard;
    private VendRequestProfile                          vendRequestProfile;
    private VendProspectiveVendorRegistrationRequest    vendProspectiveVendorRegistrationRequest;
    // Indicate initial tab page in wizard
    private int currentTabPage = 1;
    private boolean closeOk = false;
    private boolean triggerValidation = false;
    private boolean nextPrevQuestionnaireClicked = false;
    private boolean businessInformationHidden = false;
    private Email contactPersonEmail;
    private const FormControlName selectCountryTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, SelectCountryTabPage);
    private const FormControlName termsAndConditionsTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, TermsAndConditionsTabPage);
    private const FormControlName companyInformationTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, CompanyInformationTabPage);
    private const FormControlName contactInformationTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, ContactInformationTabPage);
    private const FormControlName businessInformationTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, BusinessInformationTabPage);
    private const FormControlName procurementCategoryTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, ProcurementCategoryTabPage);
    private const FormControlName questionnaireTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, QuestionnaireTabPage);
    private const FormControlName finishTabPageName = formControlStr(VendProspectiveVendorRegistrationWizard, FinishTabPage);

}

public void init()
{
    Object caller = element.args().caller();

    if (caller && caller is VendProspectiveVendorRegistrationWizard)
    {
        prospectiveVendorRegistrationWizard = caller;

        super();
        contactPersonEmail = xUserInfo::find().networkAlias;
        element.initFormDatasources();
        element.initLandingPage();
        ContactEmailAddress.text(contactPersonEmail);
    }
    else
    {
        VendProspectiveVendorRegistrationWizard::main(new Args());
        element.close();
    }
}

public SysWizard wizard()
{
    return prospectiveVendorRegistrationWizard;
}

public FormTabControl tab()
{
    return Tab;
}

public void close()
{
    if (closeOk)
    {
        VendProspectiveVendorRegistrationWizard::main(new Args());
    }

    super();
}

public void closeOk()
{
    vendProspectiveVendorRegistration.reread();
    closeOk = vendProspectiveVendorRegistration.checkForCompleteness();

    if (closeOk && this.finalizeWizard())
    {
        super();
    }
}

private boolean finalizeWizard()
{
    boolean result = false ;

    ttsbegin;
    element.triggerFormValidation();

    if (triggerValidation)
    {
        vendProspectiveVendorRegistration.setName(vendorParty.Name);
        vendProspectiveVendorRegistration.createContactInfoElectronicAddressRecords(contactPersonEmail);
        VendVendorRequestNewVendorManager::construct().createVendorRequest(vendProspectiveVendorRegistration);
        result = true;
    }
    ttscommit;
    return result;
}

public void closeCancel()
{
    if (Box::yesNo("@SCM:ProspectiveVendorRegistrationSaveRequest", DialogButton::No) == DialogButton::No)
    {
        ttsbegin;
        vendProspectiveVendorRegistration.selectForUpdate(true);
        vendProspectiveVendorRegistration.delete();
        ttscommit;
    }

    super();
}

/// <summary>
/// Retrieve name of tab page based on tab index.
/// </summary>
/// <param name = "_tabIdx">Tab page index.</param>
/// <returns>Name of the tab page.</returns>
public FormControlName tabName(int _tabIdx)
{
    return this.tab().controlNum(_tabIdx).name();
}

/// <summary>
/// Triggers the form validation by setting the IsCompleted table field through the datasource.
/// </summary>
private void triggerFormValidation()
{
    // Reset form validation method will set back the validation flag back to false once validations are complete.
    triggerValidation = true;

    vendProspectiveVendorRegistration_DS.object(FieldNum(VendProspectiveVendorRegistration, IsCompleted)).setValue(true);
    vendProspectiveVendorRegistration_DS.research();
}

/// <summary>
/// Resets the form validation variables.
/// </summary>
private void resetFormValidation()
{
    triggerValidation = false;

    vendProspectiveVendorRegistration.setRegistrationIsCompleteStatus(false);
    vendProspectiveVendorRegistration_DS.reread();
}

/// <summary>
/// Adds the specified <c>EcoResCategory</c> record for the associated <c>VendProspectiveVendorRegistration</c> record to the <c>VendProspectiveVendorProcurementCategory</c> table.
/// </summary>
/// <param name = "_category">The <c>EcoResCategory</c> to be added.</param>
public void addCategory(EcoResCategory _category)
{
    if (VendProspectiveVendorProcurementCategory::addCategory(_category, vendProspectiveVendorRegistration))
    {
        vendProspectiveVendorRegistration_ds.research();
    }
}

/// <summary>
/// Initializes the wizard landing page.
/// </summary>
private void initLandingPage()
{
    if (!vendProspectiveVendorRegistrationRequest)
    {
        this.displayStaticLandingPage("@SCM:ProspectiveVendorRegistrationRequestExpired");
    }

    else if (curext() != vendProspectiveVendorRegistrationRequest.RequestCompany)
    {
        this.displayStaticLandingPage("@SCM:VendProspectiveCompanySwitchError");
    }

    else if (vendProspectiveVendorRegistration.IsCompleted)
    {
        vendRequestProfile = VendRequestProfileHelper::findVendRequestProfile(VendFieldMetadataGroupType::VendProspectiveVendorRegistration, vendProspectiveVendorRegistration.DoingBusinessCountryRegion);
        str confirmationText = vendRequestProfile.SubmittedText ? vendRequestProfile.SubmittedText : "@SCM:ProspectiveVendorRegistrationPendingVendorRequest";
        this.displayStaticLandingPage(confirmationText);
    }
}

/// <summary>
/// Initializes the correct static text for the landing page, and removes buttons and extra tabs as needed.
/// </summary>
private void displayStaticLandingPage(str _confirmationText)
{
    mainInstruction.text(_confirmationText);
    element.displayButtonsAndInitialText(false);
    element.hideAllWizardSteps();
}

