/// <summary>
/// Get tab page control sequence number on form.
/// </summary>
/// <param name = "_controlName">Tab page name.</param>
/// <returns>Tab page control sequence number on form, if found; otherwise, â€“1.</returns>
public int getTabPageControlNum(FormControlName _controlName)
{
    for (int controlIndex = 1; controlIndex < this.tab().controlCount(); controlIndex++)
    {
        if(this.tabName(controlIndex) == _controlName)
        {
            return controlIndex;
        }
    }
    return -1;
}

/// <summary>
/// Validates whether or not a user can navigate to questionnaire and procurement category tab.
/// </summary>
public void validateQuestionnaireAndProcurementCategory()
{
    VendProspectiveVendorQuestionnaire::updateQuestionnairesForProspectiveVendorRegistration(vendProspectiveVendorRegistration.RecId, vendRequestProfile.RecId);
    questionnaires = VendProspectiveVendorQuestionnaire::getProspectiveVendorRegistrationQuestionnaires(vendProspectiveVendorRegistration.RecId);
    if (conlen(questionnaires) < 1)
    {
        this.wizard().skipTab(this.getTabPageControlNum(questionnaireTabPageName), true);
    }
    else
    {
        this.wizard().skipTab(this.getTabPageControlNum(questionnaireTabPageName), false);
    }

    VendRequestManagementParameters vendRequestManagementParameters = VendRequestManagementParameters::find();
    if (vendRequestManagementParameters.VendorCategorySelectionLevelEnabled
            && vendRequestManagementParameters.VendorCategorySelectionLevel == 0)
    {
        this.wizard().skipTab(this.getTabPageControlNum(procurementCategoryTabPageName), true);
    }
    else
    {
        this.wizard().skipTab(this.getTabPageControlNum(procurementCategoryTabPageName), false);
    }
}

/// <summary>
/// Validates whether or not a user can navigate to business information and terms and conditions tab.
/// </summary>
public void validateBusinessInfoAndAgreement()
{
    if (currentTabPage == this.getTabPageControlNum(contactInformationTabPageName)
        && this.prospectiveVendorRegistrationWizard.nextClicked)
    {
        businessInformationHidden = !this.isControlVisible(BusinessProfile)
                                    && !this.isControlVisible(TaxInfo)
                                    && !this.isControlVisible(TermsOfPayment)
                                    && !this.isControlVisible(OwnershipProfile);
    }

    if (businessInformationHidden)
    {
        this.wizard().skipTab(this.getTabPageControlNum(businessInformationTabPageName), true);
    }
    else
    {
        this.wizard().skipTab(this.getTabPageControlNum(businessInformationTabPageName), false);
    }

    if (currentTabPage == this.getTabPageControlNum(selectCountryTabPageName))
    {
        var vendorRequestProfile = VendRequestProfileHelper::findVendRequestProfile(VendFieldMetadataGroupType::VendProspectiveVendorRegistration, vendProspectiveVendorRegistration.DoingBusinessCountryRegion);
        if(!vendorRequestProfile.ShowAgreementText)
        {
            this.wizard().skipTab(this.getTabPageControlNum(termsAndConditionsTabPageName), true);
        }

    }
}

/// <summary>
/// Determines if any control in group is visible.
/// </summary>
/// <param name = "_formGroupControl">Collection of form controls.</param>
/// <returns>true if any control in group is visible; otherwise, false.</returns>
private boolean isControlVisible(FormGroupControl _formGroupControl)
{
    for(int i = 1; i < _formGroupControl.controlCount(); i++)
    {
        if(_formGroupControl.controlNum(i).IsVisible())
        {
            return true;
        }
    }
    return false;
}

/// <summary>
/// Sets the visibility of wizard's dynamically created <c>FormButtonGroupControl</c>, as well as the visibility of the wizard's initial instruction text.
/// </summary>
/// <param name = "_visible">A Boolean indicating whether or not the button and text controls should be shown.</param>
public void displayButtonsAndInitialText(boolean _visible)
{
    FormRun formRun = vendProspectiveVendorRegistration_DS.formRun();
    FormButtonGroupControl buttonGroup = formRun.control(vendProspectiveVendorRegistration_DS.formRun().controlId('BottomButtonGrp'));

    buttonGroup.visible(_visible);
}

/// <summary>
/// Hides all of the wizard steps such that they don't appear on the UI.
/// </summary>
private void hideAllWizardSteps()
{
    element.design().removeControl(element.getTabControl(selectCountryTabPageName).id());
    element.design().removeControl(element.getTabControl(termsAndConditionsTabPageName).id());
    element.design().removeControl(element.getTabControl(companyInformationTabPageName).id());
    element.design().removeControl(element.getTabControl(contactInformationTabPageName).id());
    element.design().removeControl(element.getTabControl(businessInformationTabPageName).id());
    element.design().removeControl(element.getTabControl(procurementCategoryTabPageName).id());
    element.design().removeControl(element.getTabControl(questionnaireTabPageName).id());
    element.design().removeControl(element.getTabControl(finishTabPageName).id());
}

/// <summary>
/// Initializes the form's datasources based on existing <c>VendProspectiveVendorRegistrationRequest</c> records, and then sets the ranges as needed.
/// </summary>
private void initFormDatasources()
{
    vendProspectiveVendorRegistrationRequest = VendProspectiveVendorRegistrationRequest::findByEmail(contactPersonEmail);
    if (vendProspectiveVendorRegistrationRequest)
    {
        element.initVendProspectiveVendorRegistration();
    }

    SysQuery::findOrCreateRange(vendProspectiveVendorRegistration_DS.queryBuildDataSource(), fieldNum(VendProspectiveVendorRegistration, RecId)).value(queryValue(vendProspectiveVendorRegistration.RecId));
    SysQuery::findOrCreateRange(logisticsAddressCountryRegionTranslation_DS.queryBuildDataSource(), fieldNum(LogisticsAddressCountryRegionTranslation, LanguageId)).value(queryValue(currentUserLanguage()));
}

/// <summary>
/// Initializes the wizard's <c>VendProspectiveVendorRegistration</c> record by finding existing records under the same <c>VendProspectiveVendorRegistrationRequest</c> record.
/// Inserts a new <c>VendProspectiveVendorRegistration</c> if no existing record exists for the user.
/// </summary>
private void initVendProspectiveVendorRegistration()
{
    VendProspectiveVendorRegistration vendProspectiveVendorRegistrationTmp = VendProspectiveVendorRegistration::findByRegistrationRequest(vendProspectiveVendorRegistrationRequest.RecId);

    if (vendProspectiveVendorRegistrationTmp)
    {
        vendProspectiveVendorRegistration = vendProspectiveVendorRegistrationTmp;
    }
    else
    {
        if (curext() == vendProspectiveVendorRegistrationRequest.RequestCompany)
        {
            ttsbegin;
            vendProspectiveVendorRegistration_DS.create();
            vendProspectiveVendorRegistration_DS.write();
            vendProspectiveVendorRegistrationRequest.selectForUpdate(true);
            vendProspectiveVendorRegistrationRequest.setRequestStateToRegistrationInProgress();
            vendProspectiveVendorRegistrationRequest.update();
            ttscommit;
        }
    }
}

/// <summary>
/// Validates that the Terms and Conditions checkbox is set to true.
/// </summary>
private boolean validateTermsAndConditionsCheckbox()
{
    if (vendRequestProfile.ShowAgreementText && vendRequestProfile.ShowAcceptAgreement && !acceptChecked.value() )
    {
        return checkFailed("@SCM:ProspectiveVendorRegistrationAcceptTermsAndConditions");
    }

    return true;
}

/// <summary>
/// Sets the country and <c>VendRequestProfile</c>.
/// </summary>
private void setCountryAndProfile()
{
    vendProspectiveVendorRegistration.setDoingBusinessCountryRegion(logisticsAddressCountryRegion.CountryRegionId);
    vendProspectiveVendorRegistration_DS.reread();

    vendRequestProfile = VendRequestProfileHelper::findVendRequestProfile(VendFieldMetadataGroupType::VendProspectiveVendorRegistration, vendProspectiveVendorRegistration.DoingBusinessCountryRegion);

    VendRequestProfileHelper::configureFields(vendRequestProfile.RecId, vendProspectiveVendorRegistration_DS, vendProspectiveVendorRegistration.TableId);

    // The helper method changes the VendProspectiveVendorRegistration datasource, so to avoid update conflicts, we perform a reread on the local buffer here.
    vendProspectiveVendorRegistration.reread();
}

/// <summary>
/// Sets the Accept Agreement text and checkbox.
/// </summary>
private void setAcceptedTextAndCheck()
{
    agreementText.visible(vendRequestProfile.ShowAgreementText);
    agreementText.text(vendRequestProfile.AgreementText);
    acceptChecked.label(vendRequestProfile.AcceptAgreementText);
    acceptChecked.visible(vendRequestProfile.ShowAcceptAgreement);
}

/// <summary>
/// Loads the next questionnaire record into the Questionnaire tab.
/// </summary>
/// <param name = "_nextQuestionnairePos">The next questionnaire index to be loaded.</param>
private void loadQuestionnaire(int _nextQuestionnairePos)
{
    VendProspectiveVendorQuestionnaire questionnaire = conpeek(questionnaires, _nextQuestionnairePos);
    vendProspectiveVendorQuestionnaire = VendProspectiveVendorQuestionnaire::find(questionnaire.RecId);

    currentQuestionnairePos = _nextQuestionnairePos;

    vendProspectiveVendorQuestionnaire_DS.positionToRecord(vendProspectiveVendorQuestionnaire);

    element.setQuestionnaireButtons();
    element.setQuestionnaireText();
}

/// <summary>
/// Sets the button and static text labels for the Questionnaire tab.
/// </summary>
private void setQuestionnaireText()
{
    questionnaireLabel.text(strFmt("@SCM:ProspectiveVendorRegistrationQuestionnaireMainInstruction", currentQuestionnairePos, conlen(questionnaires)));

    if (vendProspectiveVendorQuestionnaire.QuestionnaireResponse)
    {
        completeQuestionnaire.text("@SCM:ProspectiveVendorRegistrationRestartQuestionnaire");
    }
    else
    {
        completeQuestionnaire.text("@SYS305210");
    }
}

/// <summary>
/// Sets the Next, Previous, and View Answers questionnaire buttons.
/// </summary>
private void setQuestionnaireButtons()
{
    vendProspectiveVendorRegistration.reread();

    boolean isMandatoryFieldsFilled = VendRequestProfileHelper::checkMandatoryFields(vendRequestProfile.RecId, vendProspectiveVendorRegistration, !vendProspectiveVendorRegistration.IsCompleted);

    viewAnswers.enabled(vendProspectiveVendorQuestionnaire.QuestionnaireResponse != '' && isMandatoryFieldsFilled);
    nextQuestionnaire.enabled(currentQuestionnairePos < conlen(questionnaires)  && isMandatoryFieldsFilled);
    previousQuestionnaire.enabled(currentQuestionnairePos > 1  && isMandatoryFieldsFilled);
}

/// <summary>
/// Validates whether or not a user can navigate away from the Terms and Conditions tab.
/// </summary>
/// <param name = "_fromTab">The tab number to be navigated from.</param>
/// <param name = "_toTab">The tab number to be navigated to.</param>
/// <returns>true if the navigation is valid; otherwise, false.</returns>
private boolean validateNavigationFromTermsAndConditionsTab(int _fromTab, int _toTab)
{
    return !(this.tabName(_fromTab) == termsAndConditionsTabPageName
           && this.prospectiveVendorRegistrationWizard.nextClicked
           && !element.validateTermsAndConditionsCheckbox());
}

/// <summary>
/// Validates whether or not a user can navigate away from the Business Information tab.
/// </summary>
/// <param name = "_fromTab">The tab number to be navigated from.</param>
/// <param name = "_toTab">The tab number to be navigated to.</param>
/// <returns>true if the navigation is valid; otherwise, false.</returns>
private boolean validateNavigationFromBusinessInformationTab(int _fromTab, int _toTab)
{
    FormControlName fromTabName = this.tabName(_fromTab);

    if ((fromTabName == contactInformationTabPageName || fromTabName == businessInformationTabPageName)
        && this.prospectiveVendorRegistrationWizard.nextClicked)
    {
        boolean onCorrectValidationTab = businessInformationHidden ?  fromTabName == contactInformationTabPageName : fromTabName == businessInformationTabPageName;

        if (onCorrectValidationTab)
        {
            element.triggerFormValidation();

            boolean validationSuccessful = triggerValidation;

            if (triggerValidation)
            {
                element.resetFormValidation();
            }

            return validationSuccessful;
        }
    }

    return true;
}

/// <summary>
/// Validates whether or not a user can navigate away from the Questionnaires tab.
/// </summary>
/// <param name = "_fromTab">The tab number to be navigated from.</param>
/// <param name = "_toTab">The tab number to be navigated to.</param>
/// <returns>true if the navigation is valid; otherwise, false.</returns>
private boolean validateNavigationFromQuestionnairesTab(int _fromTab, int _toTab)
{
    return !(this.tabName(_fromTab) == questionnaireTabPageName
           && this.prospectiveVendorRegistrationWizard.nextClicked
           && !VendProspectiveVendorQuestionnaire::checkAllQuestionnairesCompletedForProspectiveVendorRegistration(vendProspectiveVendorRegistration.RecId));
}

/// <summary>
/// Checks if the Doing Business Country Region field has changed.
/// </summary>
/// <returns>true if changed; otherwise, false.</returns>
private boolean isDoingBusinessCountryRegionChanged()
{
    return this.tabName(currentTabPage) == selectCountryTabPageName
           && vendProspectiveVendorRegistration.DoingBusinessCountryRegion
           && logisticsAddressCountryRegion.CountryRegionId != vendProspectiveVendorRegistration.DoingBusinessCountryRegion;
}

public boolean validateWrite()
{
    if (curext() != vendProspectiveVendorRegistrationRequest.RequestCompany)
    {
        return false;
    }

    // Delay the warnings unless triggerValidation boolean has been set
    if (triggerValidation)
    {
        triggerValidation = super();

        if (!triggerValidation)
        {
            element.resetFormValidation();
        }

    }

    return true;
}

public void initValue()
{
    super();

    if (vendProspectiveVendorRegistrationRequest)
    {
        vendProspectiveVendorRegistration.initFrom(vendProspectiveVendorRegistrationRequest);
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    LogisticsAddressState::lookupStateId(_formControl, vendProspectiveVendorRegistration.DoingBusinessCountryRegion);
}

public void modified()
{
    if (!taxState.valueStr() || LogisticsAddressState::exist(vendProspectiveVendorRegistration.DoingBusinessCountryRegion, taxState.valueStr()))
    {
        super();
    }
    else
    {
        warning(strFmt(LogisticsAddressState::txtNotExist(), taxState.valueStr()));
    }
}

public void reread()
{
    super();

    element.loadQuestionnaire(currentQuestionnairePos);
}

public void tabChanged(int _fromTab, int _toTab)
{
    if (!vendProspectiveVendorRegistration.IsCompleted)
    {
        if (_fromTab == element.getTabPageControlNum(selectCountryTabPageName))
        {
            element.setCountryAndProfile();
        }

        if ((element.validateNavigationFromTermsAndConditionsTab(_fromTab, _toTab)
            && element.validateNavigationFromQuestionnairesTab(_fromTab, _toTab)
            && element.validateNavigationFromBusinessInformationTab(_fromTab, _toTab)))
        {
            element.currentTabPage = _toTab;
            super(_fromTab, _toTab);
        }
    }
}

public boolean tabChange(int _FromTab)
{

    if (element.tab().controlNum(element.currentTabPage).name() == questionnaireTabPageName
        && nextPrevQuestionnaireClicked)
    {
        nextPrevQuestionnaireClicked = false;
        return false;
    }

    if (element.isDoingBusinessCountryRegionChanged())
    {
        if (Box::yesNo(strFmt("@SCM:ProspectiveVendorRegistrationChangeCountryRegion",
                       vendProspectiveVendorRegistration.DoingBusinessCountryRegion,
                       logisticsAddressCountryRegion.CountryRegionId), DialogButton::No) == DialogButton::No)
        {
            return false;
        }
        else
        {
            vendProspectiveVendorRegistration.clearConfigurableFields();
            vendProspectiveVendorRegistration.DoingBusinessCountryRegion = logisticsAddressCountryRegion.CountryRegionId;
        }
    }

    return super(_FromTab);
}

/// <summary>
/// Execute in response to a tab page activation.
/// </summary>
public void pageActivated()
{
    if (element.vendProspectiveVendorRegistration.DoingBusinessCountryRegion)
    {
        logisticsAddressCountryRegionTranslation_DS.positionToRecord(LogisticsAddressCountryRegionTranslation::find(vendProspectiveVendorRegistration.DoingBusinessCountryRegion));
        logisticsAddressCountryRegionTranslation_DS.mark(true);
    }

    super();
}

/// <summary>
/// Execute in response to a tab page activation.
/// </summary>
public void pageActivated()
{
    element.setAcceptedTextAndCheck();
    super();
}

public void clicked()
{
    super();

    MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(vendProspectiveVendorProcurementCategory_ds);

    VendProspectiveVendorProcurementCategory category = multiSelectionHelper.getFirst();

    while (category.RecId)
    {
        VendProspectiveVendorProcurementCategory localProspectiveVendorProcurementCategory;

        delete_from localProspectiveVendorProcurementCategory
             where localProspectiveVendorProcurementCategory.RecId == category.RecId;

        category = multiSelectionHelper.getNext();
    }

    vendProspectiveVendorRegistration_DS.research();
}

/// <summary>
/// Execute in response to a tab page activation and load questionnaire.
/// </summary>
public void pageActivated()
{
    element.currentQuestionnairePos = 1;
    element.loadQuestionnaire(currentQuestionnairePos);
    super();
}

void clicked()
{
    VendProspectiveVendorQuestionnaire_KmQuestionnaireRun_Win32 kmQuestionnaireRun = new VendProspectiveVendorQuestionnaire_KmQuestionnaireRun_Win32();

    super();

    kmQuestionnaireRun.setCallingReferences(vendProspectiveVendorQuestionnaire.TableId, vendProspectiveVendorQuestionnaire.RecId);
    kmQuestionnaireRun.setCallerDataSources(vendProspectiveVendorQuestionnaire_DS);

    if (kmQuestionnaireRun.set(KMQuestionnaireRunMode::VendReq, vendProspectiveVendorQuestionnaire.QuestionnaireDefinition, DirPersonUser::current().PersonParty))
    {
        kmQuestionnaireRun.run();
    }
}

void clicked()
{
    KMVirtualNetworkAnswerTable kmVirtualNetworkAnswerTable;

    if (vendProspectiveVendorQuestionnaire.QuestionnaireResponse)
    {
        kmVirtualNetworkAnswerTable = KMVirtualNetworkAnswerTable::find(vendProspectiveVendorQuestionnaire.QuestionnaireResponse);

        if (kmVirtualNetworkAnswerTable)
        {
            Args args = new Args();

            args.record(kmVirtualNetworkAnswerTable);

            new MenuFunction(menuitemOutputStr(KMKnowledgeCollectorAnswerReportForm), MenuItemType::Output).run(args);
        }
    }
}

void clicked()
{
    super();

    element.loadQuestionnaire(currentQuestionnairePos - 1);
    nextPrevQuestionnaireClicked = true;
}

void clicked()
{
    super();

    element.loadQuestionnaire(currentQuestionnairePos + 1);
    nextPrevQuestionnaireClicked = true;
}

/// <summary>
/// Disable finish button if we do not acknowledge the Terms and Conditions
/// </summary>
public void pageActivated()
{
    super();

    if (VendRegistrationWizardConsiderFinishButtonDisablingFlight::instance().isEnabled())
    {
        boolean requiresAgreementAcceptance = vendRequestProfile.ShowAgreementText && vendRequestProfile.ShowAcceptAgreement;
        boolean shouldEnableFinishButton    = !requiresAgreementAcceptance || acceptChecked.value();

        prospectiveVendorRegistrationWizard.finishEnabled(shouldEnableFinishButton, prospectiveVendorRegistrationWizard.curTab(), shouldEnableFinishButton);
    }
}
