[Form]
public final class VendPurchOrderJournal extends FormRun
{
    InventDimCtrl_Frm_Mov           inventDimFormSetup;

}

public Common docCursor()
{
    Common docCursor = super();

    if (docCursor.TableId == tablenum(InventDim))
    {
        docCursor = purchLineAllVersions;
    }

    return docCursor;
}

void init()
{
    super();

    element.updateLedgerPostingDate();

    if (element.args().record())
    {
        purchTableAllVersions_ds.object(fieldnum(PurchTableAllVersions, InvoiceAccount)).visible(false);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (LedgerParameters::find().IsEncumbranceProcessEnabled == false)
    {
        ledgerTransact.visible(false);
        buttonSubLedgerJournalAccountEntry.visible(false);
    }

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TaxJournalTrans.visible(false);
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    PurchLine       purchLine;

    InventMovement  movement = InventMovement::construct(purchLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

/// <summary>
/// Displays or hides purchase order ledger posting date field.
/// </summary>
public void updateLedgerPostingDate()
{
    boolean showLedgerPostingDate = LedgerParameters::find().IsEncumbranceProcessEnabled == NoYes::Yes;
    vendPurchOrderJour_PurchaseOrderTransactionDate.visible(showLedgerPostingDate);
}

public void init()
{
    super();

    Args args = element.args();

    if (args.dataset() == tablenum(PurchReqLine))
    {
        this.query().dataSourceTable(tablenum(VendPurchOrderJour)).clearDynalinks();

        QueryBuildDataSource queryDataSourceLink = this.query().dataSourceName(identifierstr(VendPurchOrderJour));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldnum(VendPurchOrderJour, PurchId),
                                        args.record(),
                                        fieldnum(PurchReqLine, PurchId));
        queryDataSourceLink.addDynalink(fieldnum(VendPurchOrderJour, dataAreaId),
                                        args.record(),
                                        fieldnum(PurchReqLine, PurchIdDataArea));
    }
}

public void init()
{
    super();

    QueryBuildDataSource qbDS = this.query().dataSourceTable(this.table());
    qbDS.addLink(fieldnum(VendPurchOrderJour, PurchTableVersion), fieldnum(PurchTableAllVersions, PurchTableVersionRecId));

    if (element.args().record() && (element.args().record().TableId == tablenum(VendPurchOrderJour)))
    {
        VendPurchOrderJour localVendPurchOrderJour = element.args().record();
        qbDS.addRange(fieldnum(PurchTableAllVersions, PurchId)).value(queryValue(localVendPurchOrderJour.PurchId));
    }
}

display public LineAmountTax lineAmountTax(PurchLineAllVersions _purchLineAllVersions)
{
    return PurchTableAllVersions.InclTax ? _purchLineAllVersions.ConfirmedTaxAmount : 0;
}

public void init()
{
    super();

    QueryBuildDataSource qbDS = this.query().dataSourceTable(this.table());
    qbDS.addDynalink(fieldnum(PurchLineAllVersions, PurchTableVersionRecId), purchTableAllVersions, fieldnum(PurchTableAllVersions, PurchTableVersionRecId));

    QueryBuildRange range = qbDS.addRange(fieldnum(PurchLineAllVersions, LineDeliveryType));
    range.value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));
    range.status(RangeStatus::Hidden);
}

