}

public void clicked()
{
    PurchTableVersion   purchTableVersion;
    AccountingEvent     accountingEvent;
    PurchTable          purchTable;

    purchTable = vendPurchOrderJour.purchTable();
    purchTableVersion = PurchTableVersion::find(vendPurchOrderJour.PurchTableVersion);

    accountingEvent = AccountingEvent::findBySourceDocumentHeaderEventDateTime(purchTable.SourceDocumentHeader, purchTableVersion.VersionDateTime);

    if (accountingEvent)
    {
        super();
    }
    else
    {
        checkFailed("@SCM:PurchConfirmationVoucherValidation");
    }
}

void clicked()
{
    if (PurchTableAllVersions.IsArchived)
    {
        super();
    }
    else
    {
        Args args = new Args(formStr(MarkupTrans));
        args.caller(element);
        args.record(PurchTable::find(PurchTableAllVersions.PurchId));

        FormRun formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

