public boolean allowCategoryEdit()
{
    return vendRequest_VendRequestCategoryExtension.CategoryExtensionStatus == VendRequestCategoryStatus::CategoryRequestPendingReview ||
        vendRequest.VendRequestWorkflowState == VendRequestWorkflowState::PendingApproval;
}

