public void delete()
{
    LogisticsLocation location;
    super();
    location = LogisticsLocation::find(vendPostalAddressView.Location,true);

    if (Box::yesNo(strFmt("@SYS302359",tableId2pname(tableNum(LogisticsPostalAddress))),DialogButton::No, "@SYS24848") == DialogButton::Yes && location.validateDelete())
    {
        location.delete();
        vendPostalAddressView_DS.research();
    }
}

public void updateButtons()
{
    boolean exist;

    exist = vendPostalAddressView.RecId != 0;
    editAddress.enabled(postalAddressAllowEdit && exist);
    deleteAddress.enabled(postalAddressAllowDelete && exist && vendPostalAddressView.IsPrimary == false);
    mapButton.enabled(exist);
    newAddress.enabled(postalAddressAllowCreate && vendRequest.VendParty != 0);
    detailAddress.enabled(exist);
}

public int active()
{
    int ret;

    ret = super();
    this.updateButtons();

    return ret;
}

public void executeQuery()
{
    DirPartyContactInfoView::updateQuery(this.query().dataSourceName(this.name()), contactParty.RecId);

    super();

    this.updateButtons();

    if (contactElectronicAddressLocation != 0 && contactElectronicAddressLocation != contactElectronicAddressView.Location)
    {
        this.findValue(fieldNum(DirPartyContactInfoView,Location),SysQuery::value(contactElectronicAddressLocation));
    }
}

