/// <summary>
/// Creates a record with specific concrete types so that the type picker dialog is not shown.
/// </summary>
/// <param name="_formDataSourceName">
/// The data source to create a record for.
/// </param>
/// <param name="_append">
/// true if the created row should be appended; otherwise false.
/// </param>
public void createRecord(str _formDataSourceName, boolean _append = false)
{
    if (conFind(mainDatasources, this.dataSource(_formDataSourceName).id()))
    {
        partyForm.initValue();
        partyForm.create();
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

/// <summary>
/// Initializes the form components.
/// </summary>
public void init()
{
    #resappl

    listPanel = SysListPanelRelationTable::newForm(
        element,
        element.controlId(formControlStr(VendRequestDisallowedVendor, CompanyTabPage)),
        "@SYS58049",
        "@SYS58050",
        #ImageCompany,
        tableNum(VendRequestDisallowedVendorLegalEntity),
        fieldNum(VendRequestDisallowedVendorLegalEntity, DataArea),
        fieldNum(VendRequestDisallowedVendorLegalEntity, DisallowedVendor),
        tableNum(DataArea),
        fieldNum(DataArea, Id),
        [fieldNum(DataArea, Id), fieldNum(DataArea, Name)]
        );

    listPanel.parmItemsNeeded(1);
    listPanel.parmReloadMethod(identifierStr(companiesModified));

    typeComboBox = DirPartyFormHandler::updateTypeComboBox(element, formControlStr(VendRequestDisallowedVendor, Type));

    super();

    dirPartyTable_DS.query().dataSourceTable(tableNum(DirPartyTable)).addSortField(fieldNum(DirPartyTable, Name));

    listPanel.init();

    companyTabPage.visible(false);
    addressTabPage.visible(false);
    contactInfoTabPage.visible(false);

    //Initialize splitter

    numSeqCompany = DirParameters::find().NumSeqCompanyId;

    mainDatasources = [vendRequestDisallowedVendor_ds.id(), dirPartyTable_ds.id(), dirPartyTable_DirPerson_ds.id(), dirPersonName_ds.id(), dirPartyTable_DirOrganization_ds.id(), dirPartyTable_DirOrganizationBase_ds.id()];

    addressBooksCtrl = SysLookupMultiSelectCtrl::construct(element, addressBooks, queryStr(DirAddressBookLookup));

    partyForm = new DirPartyFormHandler_Entity(element);
    partyForm.setDatasourceCaller(vendRequestDisallowedVendor_ds);
    partyForm.setDatasourceParty(dirPartyTable_ds);
    partyForm.setDatasourcePartyName(dirPersonName_ds);
    partyForm.ctrlHideShowButton(hideShowButton);
    partyForm.ctrlHideShowComboBox(hideShowComboBox);
    partyForm.ctrlTabPageGeneral(generalTabPage);
    partyForm.parmAddressBookCtrl(addressBooksCtrl);
}

/// <summary>
/// Gets a handler to manage the number sequence for the DisallowedVendorNumber field.
/// </summary>
/// <returns>
/// A number sequence handler.
/// </returns>
public NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        changecompany(numSeqCompany)
        {
            numberSeqFormHandler = NumberSeqFormHandler::newForm(
                VendRequestManagementParameters::numRefDisallowedVendorNumber().NumberSequenceId,
                element,
                vendRequestDisallowedVendor_ds,
                fieldNum(VendRequestDisallowedVendor, DisallowedVendorNumber)
                );
        }
    }
    return numberSeqFormHandler;
}

