/// <summary>
/// Sets the controls state base on the current record.
/// </summary>
public void updateControls()
{
    boolean newRecord       = (vendRequestDisallowedVendor.RecId == 0);
    boolean newPartyRecord  = (dirPartyTable.RecId == 0);

    if (!newPartyRecord)
    {
        addressBooksCtrl.set(dirPartyTable.addressBooks());
    }

    typeComboBox.select(enum2int(dirPartyTable.baseType()));

    type.allowEdit(newRecord);
    lastPartyType = any2Enum(typeComboBox.selection());
}

/// <summary>
/// Updates the design of the form base on the type of party selected.
/// </summary>
/// <param name="_init">
/// true if being called from the init method; otherwise false.
/// </param>
public void updateDesign(boolean _init = false)
{
    boolean      newRecord      = dirPartyTable.RecId == 0;
    DirPartyType partyType      = dirPartyTable.type();
    boolean      isOrganization = (partyType == DirPartyType::Organization);
    boolean      isPerson       = (partyType == DirPartyType::Person);

    if (generalTabPage.isActivePage())
    {
        // Person
        personGroup.visible(isPerson);

        // Organization
        orgGroup.visible(isOrganization);

        typeDisplay.visible(newRecord == false);
        type.visible(newRecord == true);
        type.allowEdit(newRecord);
    }

    if (_init || generalTabPage.isActivePage())
    {
        if (_init)
        {
            partyForm.parmPartyType(partyType);
        }
        partyForm.setDatasourcePartySubType(isPerson ? dirPartyTable_DirPerson_ds : dirPartyTable_DirOrganizationBase_ds);
        partyForm.manageFields();
    }
}

