public int active()
{
    int     ret;
    boolean existingRecord;

    ret = super();

    existingRecord = vendRequestDisallowedVendor.RecId != 0;

    if (ret)
    {
        partyForm.parmPartyType(dirPartyTable.type());
        element.updateControls();
        element.updateDesign();
    }

    companyTabPage.visible(existingRecord);
    addressTabPage.visible(existingRecord);
    contactInfoTabPage.visible(existingRecord);

    listPanel.parmRelationRangeValue(queryValue(vendRequestDisallowedVendor.RecId));
    listPanel.fill();

    dirPartyPostalAddressView_ds.executeQuery();
    dirPartyContactInfoView_ds.executeQuery();

    return ret;
}

