public void write()
{
    if (!(dirPartyTable is DirPerson))
    {
        return;
    }

    if (!dirPartyTable.RecId)
    {
        dirPartyTable_ds.write();
    }

    dirPersonName.Person = dirPartyTable.RecId;

    super();
}

public boolean validateWrite()
{
    boolean ret = true;

    if (dirPartyTable is DirPerson)
    {
        ret = super() && dirPartyTable_ds.validateWrite();
    }

    return ret;
}

