public void closeOk()
{
    super();

    VendTmpCurrentVendorResult selectedVendTmpCurrentVendorResult = getFirstSelection(vendTmpCurrentVendorResult_ds);
    List vendAccountNumList = new List(Types::String);

    if (parentObject != null
        && parentObject.name() == formStr(VendUnsolicitedVendor))
    {
        parentObject.addVendorFromVendorSearch(selectedVendTmpCurrentVendorResult.VendorDataAreaId, selectedVendTmpCurrentVendorResult.VendorAccountNum);
    }
    else
    {
        while (selectedVendTmpCurrentVendorResult)
        {
            vendAccountNumList.addEnd(selectedVendTmpCurrentVendorResult.VendorAccountNum);
            selectedVendTmpCurrentVendorResult = vendTmpCurrentVendorResult_ds.getNext();
        }

        if (parentObject != null && (parentObject.name() == formStr(PurchRFQCaseTable)))
        {
            parentObject.addVendorsFromVendorSearch(vendAccountNumList);
        }
    }
}

public void deleteSelectedRecord(FormDataSource _vendorTypeDS)
{
    if (_vendorTypeDS.anyMarked())
    {
        _vendorTypeDS.deleteMarked();
    }
    else
    {
        _vendorTypeDS.delete();
    }
    _vendorTypeDS.research();
}

/// <summary>
/// Executes the return queries for each data sources
/// </summary>
public void executeQuery()
{
    VendTmpCurrentVendorResult          currentVendor;
    VendTmpProspectiveVendorResult      prospectiveVendor;
    VendTmpUnsolicitedVendorResult      unsolicitedVendor;
    QueryRun                            qrCurrent, qrProspective, qrUnsolicited;
    VendTable                           vendTable;
    VendRequest                         vendRequest;
    DirPartyTable                       dirPartyTable;
    LogisticsPostalAddress              logisticsPostalAddress;
    VendRequestSignup                   vendRequestSignup;
    int                                 currentVendorCount;
    int                                 prospectiveVendorCount;
    int                                 unsolicitedVendorCount;

    //Current Vendor Grid Refill Section
    delete_from vendTmpCurrentVendorResult;
    currentVendorsTab.visible(false);

    if (queryCurrentVendor != null)
    {
        currentVendor.setTmp();

        queryCurrentVendor.allowCrossCompany(isAllLegalEntitiesIncluded);

        if (!isAllLegalEntitiesIncluded && dataAreaId != '' && dataAreaId != curext())
        {
            queryCurrentVendor.allowCrossCompany(true);
            queryCurrentVendor.addCompanyRange(dataAreaId);
        }

        qrCurrent = new QueryRun(queryCurrentVendor);
        currentVendorCount = 0;
        while (qrCurrent.next())
        {
            vendTable = qrCurrent.get(tableNum(VendTable));
            dirPartyTable = qrCurrent.get(tableNum(DirPartyTable));

            currentVendor.clear();
            currentVendor.VendorAccountNum = vendTable.AccountNum;
            currentVendor.Name = dirPartyTable.Name;
            currentVendor.SearchName = dirPartyTable.NameAlias;
            currentVendor.Blocked = vendTable.Blocked;
            currentVendor.VendorDataAreaId = vendTable.DataAreaId;
            currentVendor.VendTableRecId = vendTable.RecId;
            currentVendor.insert();
            currentVendorCount++;
        }
        vendTmpCurrentVendorResult.setTmpData(currentVendor);
        vendTmpCurrentVendorResult_ds.research();
        currentVendorsTab.visible(true);
        this.selectControl(gridCurrentVendors);
        currentVendorActionPane.enabled(currentVendorCount > 0);
    }

    //Prospective Vendor Grid Refill Section
    delete_from vendTmpProspectiveVendorResult;
    prospectiveVendorsTab.visible(false);

    if (queryProspectiveVendor != null
        && isConfigurationkeyEnabled(configurationKeyNum(VendRequest)))
    {
        prospectiveVendor.setTmp();
        if (isAllLegalEntitiesIncluded)
        {
            queryProspectiveVendor.allowCrossCompany(true);
        }

        qrProspective = new QueryRun(queryProspectiveVendor);
        prospectiveVendorCount = 0;
        while (qrProspective.next())
        {
            vendRequest = qrProspective.get(tableNum(VendRequest));
            dirPartyTable = qrProspective.get(tableNum(DirPartyTable));
            logisticsPostalAddress = qrProspective.get(tableNum(LogisticsPostalAddress));

            prospectiveVendor.clear();
            prospectiveVendor.AccountNum = vendRequest.AccountNum;
            prospectiveVendor.Name = dirPartyTable.Name;
            prospectiveVendor.SearchName = dirPartyTable.NameAlias;
            prospectiveVendor.City = logisticsPostalAddress.City;
            prospectiveVendor.CountryRegionId = logisticsPostalAddress.CountryRegionId;
            prospectiveVendor.VendorRequestDataAreaId = vendRequest.DataAreaId;
            prospectiveVendor.VendRequestNumber = vendRequest.VendRequestNumber;
            prospectiveVendor.VendRequestRecId = vendRequest.RecId;
            prospectiveVendor.insert();
            prospectiveVendorCount++;
        }
        vendTmpProspectiveVendorResult.setTmpData(prospectiveVendor);
        vendTmpProspectiveVendorResult_ds.research();
        prospectiveVendorsTab.visible(true);
        prospectiveVendorActionPane.enabled(prospectiveVendorCount > 0);
        if (this.selectedControl())
        {
            this.selectControl(gridProspectiveVendors);
        }
    }

    //Unsolicited Vendor Grid Refill Section
    delete_from vendTmpUnsolicitedVendorResult;
    unsolicitedVendorsTab.visible(false);

    if (queryUnsolicitedVendor != null
        && isConfigurationkeyEnabled(configurationKeyNum(VendUnsolicitedRegistration)))
    {
        unsolicitedVendor.setTmp();
        queryUnsolicitedVendor.allowCrossCompany(true);

        qrUnsolicited = new QueryRun(queryUnsolicitedVendor);
        unsolicitedVendorCount = 0;
        while (qrUnsolicited.next())
        {
            vendRequestSignup = qrUnsolicited.get(tableNum(VendRequestSignup));

            unsolicitedVendor.clear();
            unsolicitedVendor.Name = vendRequestSignup.Name;
            unsolicitedVendor.SearchName = vendRequestSignup.Name;
            unsolicitedVendor.City = vendRequestSignup.City;
            unsolicitedVendor.CountryRegionId = vendRequestSignup.CountryRegionId;
            unsolicitedVendor.RequestSignUp = vendRequestSignup.RecId;
            unsolicitedVendor.insert();
            unsolicitedVendorCount++;
        }
        vendTmpUnsolicitedVendorResult.setTmpData(unsolicitedVendor);
        vendTmpUnsolicitedVendorResult_ds.research();
        unsolicitedVendorsTab.visible(true);
        unsolicitedVendorActionPane.enabled(unsolicitedVendorCount > 0 ? true : false);
        if (this.selectedControl())
        {
            this.selectControl(gridUnsolicitedVendors);
        }
    }
}

public VendAccount getSelectedVendorAccountNumber()
{
    VendTmpCurrentVendorResult selectedVendTmpCurrentVendorResult  = getFirstSelection(vendTmpCurrentVendorResult_ds);
    VendAccount accountNumber = selectedVendTmpCurrentVendorResult.VendorAccountNum;

    return accountNumber;
}

public List getSelectedVendorAccountNumberList()
{
    VendTmpCurrentVendorResult selectedVendTmpCurrentVendorResult  = getFirstSelection(vendTmpCurrentVendorResult_ds);
    List vendAccountNumList = new List(Types::String);
    while (selectedVendTmpCurrentVendorResult)
    {
        vendAccountNumList.addEnd(selectedVendTmpCurrentVendorResult.VendorAccountNum);
        selectedVendTmpCurrentVendorResult = vendTmpCurrentVendorResult_ds.getNext();
    }

    return vendAccountNumList;
}

public void init()
{
    super();

    if (element.args().caller())
    {
        Object callerObject = element.args().caller();
        parentObject = callerObject.parmParentObject();
    }
    currentVendorActionPane.enabled(false);
    prospectiveVendorActionPane.enabled(false);
    unsolicitedVendorActionPane.enabled(false);
    okCancel.visible(simplifiedSearchResult);
    currentVendorActionPane.visible(!simplifiedSearchResult);
    gridCurrentVendors.multiSelect(!simplifiedSearchResult);
    vendSearchHelper = new VendSearchHelper();
}

