private boolean isCurrentVendorBlockedForRequisition()
{
    VendTmpCurrentVendorResult selectedVendTmpCurrentVendorResult = getFirstSelection(vendTmpCurrentVendorResult_ds);
    if (selectedVendTmpCurrentVendorResult.Blocked == CustVendorBlocked::All ||
        selectedVendTmpCurrentVendorResult.Blocked == CustVendorBlocked::Requisition)
    {
        return true;
    }

    return false;
}

public void init()
{
    super();
    vendTableForTransaction_ds.init();
}

private boolean isSelectedVendorFromCurrentCompany()
{
    VendTmpCurrentVendorResult selectedVendTmpCurrentVendorResult = getFirstSelection(vendTmpCurrentVendorResult_ds);
    DataAreaId currentVendorDataAreaId = selectedVendTmpCurrentVendorResult.VendorDataAreaId;

    return (currentVendorDataAreaId == CompanyInfo::find().DataArea);
}

public int active()
{
    int ret = super();

    boolean isOnHold = this.isCurrentVendorBlockedForRequisition();
    boolean isMultipleLeSelected = this.isCurrentVendorFromSameLE();
    requestForQuote.enabled(isMultipleLeSelected);

    boolean isAllSelectedLECurrentLE = this.isSelectedVendorFromCurrentCompany();
    if (isMultipleLeSelected)
    {
        requestForQuote.enabled(isAllSelectedLECurrentLE && !isOnHold);
    }
    purchaseOrder.enabled(isAllSelectedLECurrentLE);
    dialogCopyVendor.enabled(!isAllSelectedLECurrentLE);

    return ret;
}

private boolean isCurrentVendorFromSameLE()
{
    VendTmpCurrentVendorResult selectedVendTmpCurrentVendorResult = getFirstSelection(vendTmpCurrentVendorResult_ds);
    DataAreaId currentVendorDataAreaId = selectedVendTmpCurrentVendorResult.VendorDataAreaId;
    while (selectedVendTmpCurrentVendorResult)
    {
        if (currentVendorDataAreaId != selectedVendTmpCurrentVendorResult.VendorDataAreaId)
        {
            return false;
        }
        selectedVendTmpCurrentVendorResult = vendTmpCurrentVendorResult_ds.getNext();
    }

    return true;
}

public void init()
{
    super();

    vendTableForTransaction_ds.query().dataSourceTable(tableNum(VendTable)).addRange(fieldNum(VendTable, AccountNum));
    vendTableForTransaction_ds.query().dataSourceTable(tableNum(VendTable)).addRange(fieldNum(VendTable, DataAreaId));
    vendTableForTransaction_ds.query().dataSourceTable(tableNum(VendTable)).addRange(fieldNum(VendTable, RecId));
}

