void clicked()
{
    Args        args;
    FormRun     formRun;
    Object      formRunObj;

    args = new Args();
    args.name(formStr(VendSearchCriterion));
    args.caller(element);

    formRun = classfactory.formRunClass(args);
    formRunObj = formRun;

    if (simplifiedSearchResult)
    {
        if (formHasMethod(formRun, identifierStr(parmSimplifiedSearchCriteria)))
        {
            formRunObj.parmSimplifiedSearchCriteria(simplifiedSearchResult);
            formRunObj.parmDataAreaId(dataAreaId);
        }
    }

    formRun.run();
    formRun.wait(true);
}

void clicked()
{
    VendTmpCurrentVendorResult vendTmpCurrentVendorsResultDeleted;

    super();
    element.deleteSelectedRecord(vendTmpCurrentVendorResult_ds);

    vendTmpCurrentVendorsResultDeleted.setTmpData(vendTmpCurrentVendorResult);
    select count(RecId) from vendTmpCurrentVendorsResultDeleted;

    if (vendTmpCurrentVendorsResultDeleted.RecId == 0)
    {
        currentVendorActionPane.enabled(false);
    }
}

void clicked()
{
    vendTableForTransaction_ds.query().dataSourceTable(tableNum(VendTable)).findRange(fieldNum(VendTable, RecId)).value(queryValue(vendTmpCurrentVendorResult.VendTableRecId));
    vendTableForTransaction_ds.executeQuery();

    super();
}

void clicked()
{
    vendTableForTransaction_ds.query().dataSourceTable(tableNum(VendTable)).findRange(fieldNum(VendTable, RecId)).value(queryValue(vendTmpCurrentVendorResult.VendTableRecId));
    vendTableForTransaction_ds.executeQuery();

    super();
}

public void jumpRef()
{
    if (VendTmpCurrentVendorResult.VendorDataAreaId != curExt()
        && VendorSearchResultsAccountNumFieldChangesCompanyFlight::instance().isEnabled())
    {
        appl.setDefaultCompany(vendTmpCurrentVendorResult.VendorDataAreaId, false);

        super();

        warning(strFmt("@SYS53441", vendTmpCurrentVendorResult.VendorDataAreaId));
    }
    else
    {
        super();
    }
}

