[Form]
public class VendServiceTaxPOTReverseReclaim_IN extends FormRun
{
    QueryRun                                queryRun;
    boolean                                 allowExecuteQuery;
    QueryBuildDataSource                    qbdsVendTransServiceExt;
    TmpVendTransServiceTaxExtLinkTable_IN   tmpVendTransServiceTaxExtLinkTable;
    QueryBuildRange                         queryBuildRangeAccountNum;
    QueryBuildRange                         queryBuildRangeInvoice;
    QueryBuildRange                         queryBuildRangeTransDate;
    QueryBuildRange                         queryBuildRangeSettleAmountCur;
    QueryBuildRange                         queryBuildRangeAmountCur;
    QueryBuildRange                         queryBuildRangeServiceTaxPOTStatus;
    ServiceTaxPOTInquiry_Reversal_IN        serviceTaxPOTInquiryReversal;
    ServiceTaxPOTInquiry_Reclaim_IN         serviceTaxPOTInquiryReclaim;
    ServiceTaxPOTInquiry_IN                 serviceTaxPOTInquiry;

}

void enableButtons()
{
    TaxServiceTaxPOTReversal_IN.enabled(InvoiceType.selection() == any2int(InvoiceType_IN::OpenInvoices));
    TaxServiceTaxPOTReclaim_IN.enabled(InvoiceType.selection()  == any2int(InvoiceType_IN::RealizedInvoices));
}

public void enableFieldsForRealizedInvoices()
{
    boolean isRealizedInvoices;

    isRealizedInvoices = InvoiceType.selection() == any2int(InvoiceType_IN::RealizedInvoices) ? true : false;
    VendTrans_SettleAmountCur.visible(!isRealizedInvoices);
    amtToSettle.visible(!isRealizedInvoices);
    AmountSettledBeforeReversal.visible(isRealizedInvoices);
    AmountSettledAfterReversal.visible(isRealizedInvoices);
    TaxToReclaimBaseAmt.visible(isRealizedInvoices);
    vendTrans_W_ds.object(fieldNum(VendTrans_W, TaxReversedBaseAmount_IN)).visible(isRealizedInvoices);
    vendTrans_W_ds.object(fieldNum(VendTrans_W, TaxReclaimedBaseAmount_IN)).visible(isRealizedInvoices);
}

/// <summary>
/// Enables/Disables the 'select' check box on reversal/reclaim form.
/// </summary>
public void enableSelectCheckBox()
{
    if (InvoiceType.selection() == any2int(InvoiceType_IN::OpenInvoices))
    {
        selectRecord.enabled(vendTrans_W.daysOpen() > 0);
    }
    else
    {
        selectRecord.enabled(vendTrans_W.taxToReclaimBaseAmount() > 0);
    }
}

void findTmpVendTransExtLinkTable(TmpVendTransServiceTaxExtLinkTable_IN _tmpVendTransServiceTaxExtLinkTable, recId _recId)
{
    select _tmpVendTransServiceTaxExtLinkTable
        where _tmpVendTransServiceTaxExtLinkTable.VendTransServiceTaxExtension == _recId;
}

public void init()
{
    Object caller = element.args().caller();

    super();

    if (TaxParameters::isServiceTaxEnable_IN() == false)
    {
        throw error(strFmt("@SYS4082391", "@GLS5006"));
    }

    element.enableButtons();
    element.enableFieldsForRealizedInvoices();
    element.enableSelectCheckBox();
    PostingDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));

    if (caller is ServiceTaxPOTInquiry_IN)
    {
        serviceTaxPOTInquiry = caller as ServiceTaxPOTInquiry_IN;
    }
}

public void setRangeValues()
{
    SysQuery::findOrCreateRange(vendTrans_ds.query().dataSourceTable(tableNum(VendTrans)),
                                fieldNum(VendTrans, Invoice)).value(SysQuery::valueNotEmptyString());
    SysQuery::findOrCreateRange(vendTrans_ds.query().dataSourceTable(tableNum(VendTrans)),
                                fieldNum(VendTrans, AccountNum)).value(Account.text());
    SysQuery::findOrCreateRange(vendTrans_ds.query().dataSourceTable(tableNum(VendTrans)),
                                fieldNum(VendTrans, AmountCur)).value(queryvalue('<0'));
    SysQuery::findOrCreateRange(vendTrans_ds.query().dataSourceTable(tableNum(VendTrans)),// All records above the start date will be fetched.
                                fieldNum(VendTrans, TransDate)).value((strfmt('(TransDate >= %1)' , Date2StrXpp(StartDate.dateValue()))));

    if (InvoiceType.selection() == any2int(InvoiceType_IN::OpenInvoices))
    {
        //Reversal. Unpaid
        SysQuery::findOrCreateRange(vendTrans_ds.query().dataSourceTable(tableNum(VendTrans)),
                                    fieldNum(VendTrans, SettleAmountCur)).value(strfmt('(SettleAmountCur != AmountCur)'));
        SysQuery::findOrCreateRange(VendTrans_W_ds.query().dataSourceTable(tableNum(VendTrans_W)),
                                    fieldNum(VendTrans_W, ServiceTaxPOTStatus_IN)).value(queryValue(ServiceTaxPOTStatus_IN::Original));
    }
    else// Reclaim. Realized ie, Paymnt done
    {
        SysQuery::findOrCreateRange(vendTrans_ds.query().dataSourceTable(tableNum(VendTrans)),
                                    fieldNum(VendTrans, SettleAmountCur)).value(strfmt(''));
        SysQuery::findOrCreateRange(VendTrans_W_ds.query().dataSourceTable(tableNum(VendTrans_W)),
                                    fieldNum(VendTrans_W, ServiceTaxPOTStatus_IN)).value(strfmt('((ServiceTaxPOTStatus_IN == %1) || (ServiceTaxPOTStatus_IN == %2) ||  (ServiceTaxPOTStatus_IN == %3))',
                                    any2int(ServiceTaxPOTStatus_IN::Reversed),
                                    any2int(ServiceTaxPOTStatus_IN::PartiallyReversed),
                                    any2int(ServiceTaxPOTStatus_IN::PartiallyReclaimed)));
    }
}

void writeTmpTransServiceTaxExtLinkTable(TmpVendTransServiceTaxExtLinkTable_IN _tmpVendTransServiceTaxExtLinkTable, recId _recId)
{
    _tmpVendTransServiceTaxExtLinkTable.VendTransServiceTaxExtension = _recId;
    _tmpVendTransServiceTaxExtLinkTable.insert();
}

edit NoYes markLines(
    boolean      _set,
    VendTrans_W  _vendTransW,
    NoYes        _markLine)
{
    NoYes  markLine;

    if (_set)
    {
        if (_markLine)
        {
            element.writeTmpTransServiceTaxExtLinkTable(tmpVendTransServiceTaxExtLinkTable,
                                                        _vendTransW.RecId);
            markLine   = NoYes::Yes;
        }
        else
        {
            tmpVendTransServiceTaxExtLinkTable.delete();
            markLine   = NoYes::No;
        }
    }
    else
    {
        element.findTmpVendTransExtLinkTable(tmpVendTransServiceTaxExtLinkTable, _vendTransW.RecId);
        if (tmpVendTransServiceTaxExtLinkTable)
        {
            markLine   = NoYes::Yes;
        }
    }

    return markLine;
}

