void clicked()
{
    if (!startDate.dateValue())
    {
        throw error("@SYS4082394");
    }
    else
    {
        allowExecuteQuery = true;
        element.setRangeValues();
        element.enableFieldsForRealizedInvoices();
        vendTable_ds.executeQuery();
        vendTrans_ds.executeQuery();
        vendTrans_W_ds.executeQuery();
    }
}

public boolean validate()
{
    boolean ret = super();

    if (ret && Account.text() && !VendTable::exist(Account.text()))
    {
        warning(strfmt("@SYS112224", Account.text(),"@SYS24500", "@SYS9449"));
        Account.text("");
        ret = false;
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if (!ret)
    {
        PostingDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.enableButtons();
    element.enableFieldsForRealizedInvoices();

    return ret;
}

void clicked()
{
    if (serviceTaxPOTInquiry)
    {
        serviceTaxPOTInquiry.performReversal(tmpVendTransServiceTaxExtLinkTable, PostingDate.dateValue());
    }
    tmpVendTransServiceTaxExtLinkTable = null;
    VendTrans_W_ds.research();
}

void clicked()
{
    if (serviceTaxPOTInquiry)
    {
        serviceTaxPOTInquiry.performReclaim(tmpVendTransServiceTaxExtLinkTable, PostingDate.dateValue());
    }
    tmpVendTransServiceTaxExtLinkTable = null;
    VendTrans_W_ds.research();
}
