[Form]
public class VendSettlementTax1099 extends FormRun
{
    boolean deleteEnabled;
    str fromVendor;
    str toVendor;

    str queryConditions;

}

void enableFields(boolean _deleteEnabled, boolean _manualTransaction)
{
    VendSettlementTax1099_ds.allowDelete(_deleteEnabled);

    Reference_VendAccount.allowEdit(_manualTransaction);
    Reference1_VendAccount.allowEdit(_manualTransaction);

    Reference_TransDate.allowEdit(_manualTransaction);
    Reference1_TransDate.allowEdit(_manualTransaction);
}

public void init()
{
    queryConditions = '';
    deleteEnabled = false;
    super();
}

/// <summary>
/// Enables fields if the appropriate 1099 box is used (G-2 or G-1).
/// </summary>
void setAllowEdit1099GFields()
{
    boolean canEdit;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        canEdit = (Tax1099Fields::find(VendSettlementTax1099.Tax1099Fields).Tax1099Box == Tax1099G::incomeTaxRefundsCreditsOffsets().tax1099Box);

        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099TradeOrBusiness)).allowEdit(canEdit);
        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099TaxYear)).allowEdit(canEdit);

        canEdit = (Tax1099Fields::find(VendSettlementTax1099.Tax1099Fields).Tax1099Box == Tax1099G::unemploymentCompensation().tax1099Box);

        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, State)).allowEdit(canEdit);
        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099StateTaxID)).allowEdit(canEdit);
        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099StateTaxWithheld)).allowEdit(canEdit);
    }
}

/// <summary>
/// Enables fields if the appropriate 1099 box is used (S-2).
/// </summary>
void setAllowEdit1099SFields()
{
    boolean canEdit;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        canEdit = (VendSettlementTax1099.Tax1099Fields == Tax1099Base::findTax1099FieldsRecId(Tax1099S::grossProceeds()));

        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099DateOfClosing)).allowEdit(canEdit);
        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099AddressOrLegalDesc)).allowEdit(canEdit);
        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099PropertyOrServices)).allowEdit(canEdit);
        vendSettlementTax1099_ds.object(fieldNum(VendSettlementTax1099, Tax1099BuyersTax)).allowEdit(canEdit);
    }
}

public int task(int _taskId)
{
    #Task
    int ret = super(_taskId);

    // Handle refresh tasks
    if (_taskId == #taskF5 || _taskId == #taskRefresh)
    {
        VendSettlementTax1099_ds.init();
        VendSettlementTax1099_ds.executeQuery();
    }

    return ret;
}

public void close()
{
    VendITax1099View vendITax1099View = element.args().caller() as VendITax1099View;

    if (vendITax1099View != null)
    {
        vendITax1099View.updateFromSubform();
    }

    super();
}

public int active()
{
    int ret = super();

    if (VendSettlementTax1099.ManualTransaction)
    {
        // If there is only one record selected (< 2), enable deletion
        deleteEnabled = deleteEnabled || VendSettlementTax1099_ds.recordsMarked().lastIndex() < 2;
    }
    else
    {
        deleteEnabled = false;
    }

    element.enableFields(deleteEnabled, VendSettlementTax1099.ManualTransaction);

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.setAllowEdit1099GFields();
        element.setAllowEdit1099SFields();
    }

    return ret;
}

public void write()
{
    super();

    VendSettlementTax1099 localVendSettlementTax1099;
    localVendSettlementTax1099.data(vendSettlementTax1099);

    // Add the current record to our query as an exemption
    if (queryConditions != '')
    {
        queryConditions += strFmt(' || (%1 == %2)', fieldStr(VendSettlementTax1099, RecId), VendSettlementTax1099.RecId);
        this.query().dataSourceTable(tableNum(VendSettlementTax1099)).clearRanges();
        this.query().dataSourceTable(tableNum(VendSettlementTax1099)).addRange(fieldNum(VendSettlementTax1099, ManualTransaction)).value(strFmt('(%1)', queryConditions));
    }

    vendSettlementTax1099_ds.executeQuery();
    vendSettlementTax1099_ds.positionToRecord(localVendSettlementTax1099);
}

/// <summary>
/// Set up the query object to filter for coming from a vendor transaction form.
/// </summary>
/// <remarks>
/// Filter on Vendor Account, Voucher, and Invoice ID from the vendor transaction form.
/// </remarks>
void filterOnVendTrans()
{
    VendTrans vendTrans = element.args().record();
    queryConditions += strFmt('(%1 == "%2")', fieldStr(VendSettlementTax1099, VendAccount), queryValue(vendTrans.AccountNum));
    fromVendor = vendTrans.AccountNum;
    toVendor = vendTrans.AccountNum;

    queryConditions += strFmt(' && (%1 == "%2")', fieldStr(VendSettlementTax1099, Voucher), queryValue(vendTrans.Voucher));

    if (vendTrans.Invoice != '')
    {
        queryConditions += strFmt(' && (%1 == "%2")', fieldStr(VendSettlementTax1099, VendInvoiceId), queryValue(vendTrans.Invoice));
    }

    this.query().dataSourceTable(tableNum(VendSettlementTax1099)).addRange(fieldNum(VendSettlementTax1099, RecId)).value(strFmt('(%1)', queryConditions));
}

/// <summary>
/// Set up the query object to filter transactions based on the line on Vendor Settlements for 1099s form.
/// </summary>
/// <remarks>
/// Filter on transaction date within the range from the form,
/// if a record isn't selected only filter on vendor account within range from form,
/// else filter on vendor account and 1099 field type, as well as
/// if the 1099 Type is G filter on 1099 Tax Year,
/// if the 1099 Type is S filter on Vendor Invoice Id.
/// </remarks>
void filterOnTax1099Summary()
{
    VendITax1099View vendITax1099View = element.args().caller() as VendITax1099View;

    if (vendITax1099View != null)
    {
        TransDate fromDate = vendITax1099View.fromDate();
        TransDate toDate = vendITax1099View.toDate();
        fromVendor = vendITax1099View.fromVendor();
        toVendor = vendITax1099View.toVendor();

        Tax1099Summary tax1099Summary = element.args().record();

        // Transaction date filter
        queryConditions += strFmt('(%1 >= %2)', fieldStr(VendSettlementTax1099, TransDate), date2StrXpp(fromDate));
        queryConditions += strFmt(' && (%1 <= %2)', fieldStr(VendSettlementTax1099, TransDate), date2StrXpp(toDate));

        if (tax1099Summary)
        {
            queryConditions += strFmt(' && (%1 == "%2")', fieldStr(VendSettlementTax1099, VendAccount), queryValue(tax1099Summary.VendAccount));
            fromVendor = tax1099Summary.VendAccount;
            toVendor = tax1099Summary.VendAccount;

            queryConditions += strFmt(' && (%1 == %2)', fieldStr(VendSettlementTax1099, Tax1099Fields), tax1099Summary.Tax1099Fields);

            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                // Additional filters for specific 1099s.
                switch (Tax1099Fields::find(tax1099Summary.Tax1099Fields).Tax1099Type)
                {
                    // If Summary info selected is amongst 1099-G or 1099-S.
                    case Tax1099Type::F1099G:
                        // include Tax Year Filter as 1099-G is summarized for each Tax1099GTaxYear.
                        queryConditions += strFmt(' && (%1 == %2)', fieldStr(VendSettlementTax1099, Tax1099TaxYear), tax1099Summary.Tax1099TaxYear);
                        break;

                    case Tax1099Type::F1099S:
                        // Include Vendor Invoice filter as 1099-S is summarized for each vendor invoice.
                        queryConditions += strFmt(' && (%1 == "%2")', fieldStr(VendSettlementTax1099, VendInvoiceId), tax1099Summary.VendInvoiceId);
                        break;
                }
            }
        }
        else
        {
            queryConditions += strFmt(' && (%1 >= "%2")', fieldStr(VendSettlementTax1099, VendAccount), queryValue(fromVendor));
            queryConditions += strFmt(' && (%1 <= "%2")', fieldStr(VendSettlementTax1099, VendAccount), queryValue(toVendor));
        }

        this.query().dataSourceTable(tableNum(VendSettlementTax1099)).addRange(fieldNum(VendSettlementTax1099, RecId)).value(strFmt('(%1)', queryConditions));
    }
}

public void init()
{
    /* Determine the filter to apply based on the calling form and button
        This form can be called in one of four ways:
        1. Directly
        2. From the Tax1099Summary form (Tax 1099 Transactions button)
        3. From the Tax1099Summary form (Manual 1099 Transactions button)
        4. From the VendTrans form
        We want to filter the data if called by methods 2 - 4.
    */

    super();

    this.query().dataSourceTable(tableNum(VendSettlementTax1099)).clearRanges();
    queryConditions = '';
    switch (element.args().dataset())
    {
        case tableNum(Tax1099Summary):
            if (element.args().menuItemName() == menuitemDisplayStr(Tax1099Details)) // 2
            {
                this.filterOnTax1099Summary();
            }
            else if (element.args().menuItemName() == menuitemDisplayStr(Tax1099Manual)) // 3
            {
                this.filterOnManual();
            }
            break;

        case tableNum(VendTrans): // 4
            this.filterOnVendTrans();
            break;
    }

    this.query().dataSourceTable(tableNum(VendSettlementTax1099)).addOrderByField(fieldNum(VendTable, AccountNum), SortOrder::Ascending);
}

/// <summary>
/// Set up the query object to filter for manualy entered transactions.
/// </summary>
/// <remarks>
/// Filter on the record being a manually entered transaction, the Transaction Date, and the Vendor Account.
/// </remarks>
void filterOnManual()
{
    VendITax1099View vendITax1099View = element.args().caller() as VendITax1099View;

    if (vendITax1099View != null)
    {
        TransDate fromDate = vendITax1099View.fromDate();
        TransDate toDate = vendITax1099View.toDate();
        fromVendor = vendITax1099View.fromVendor();
        toVendor = vendITax1099View.toVendor();

        queryConditions += strFmt('(%1 == %2)', fieldStr(VendSettlementTax1099, ManualTransaction), any2int(NoYes::Yes));

        queryConditions += strFmt(' && (%1 >= %2)', fieldStr(VendSettlementTax1099, TransDate), date2StrXpp(fromDate));
        queryConditions += strFmt(' && (%1 <= %2)', fieldStr(VendSettlementTax1099, TransDate), date2StrXpp(toDate));

        // Manual transaction ignores the currently selected vendor context, uses range from calling form
        queryConditions += strFmt(' && (%1 >= "%2")', fieldStr(VendSettlementTax1099, VendAccount), queryValue(fromVendor));
        queryConditions += strFmt(' && (%1 <= "%2")', fieldStr(VendSettlementTax1099, VendAccount), queryValue(toVendor));

        this.query().dataSourceTable(tableNum(VendSettlementTax1099)).addRange(fieldNum(VendSettlementTax1099, ManualTransaction)).value(strFmt('(%1)', queryConditions));
    }
}

public boolean validateDelete()
{
    boolean ret = super();

    VendSettlementTax1099.reread();

    if (!VendSettlementTax1099.ManualTransaction)
    {
        return false;
    }

    return ret;
}

