public boolean validate()
{
    boolean ret = super();

    if (!VendTable::find(VendSettlementTax1099.VendAccount).Tax1099Reports)
    {
        ret = ret && checkFailed(strFmt("@AccountsPayable:Tax1099VendorNotSetup", VendSettlementTax1099.VendAccount));
    }

    return ret;
}

public str resolveAmbiguousReference(FormControl _formControl)
