}

public void init()
{
    QueryBuildRange tax1099Reports;

    super();

    // Only filter if not called directly.
    if (element.args().dataset())
    {
        tax1099Reports = this.query().dataSourceTable(tableNum(VendTable)).addRange(fieldNum(VendTable, Tax1099Reports));
        tax1099Reports.value(queryValue(NoYes::Yes));
    }
}
