[Form]
public class VendSpecTransOverview extends FormRun
{
    VendSpecTransOverviewFormMode lastSelection = VendSpecTransOverviewFormMode::Journal;
    Map mapSelection2TabControl;

}

public void init()
{
    super();

    mapSelection2TabControl = new Map(Types::Enum, Types::Class);
    mapSelection2TabControl.insert(VendSpecTransOverviewFormMode::Journal, tabJournals);
    mapSelection2TabControl.insert(VendSpecTransOverviewFormMode::PurchaseOrder, tabPurchase);
    mapSelection2TabControl.insert(VendSpecTransOverviewFormMode::PaymentProposal, tabPaymProposal);

    // Disable the company accounts field on the general tab if Intercompany transactions are disabled
    if (!isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        ledgerJournalTrans_Company.allowEdit(false);
    }

    LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_AccountNum1.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_OffsetAccount1.parmPostingType(LedgerPostingType::LedgerJournal);
    ProposalLine_PaymAccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    PaymAccount_PaymAccountNum.parmPostingType(LedgerPostingType::LedgerJournal);

    element.design().caption("@SYS53650");
}

public void run()
{
    super();

    VendSpecTransOverviewFormMode documentType = this.findDocumentWithSpecTrans();
    this.changeDocumentSelection(documentType);
}

private VendSpecTransOverviewFormMode findDocumentWithSpecTrans()
{
    VendSpecTransOverviewFormMode documentType;

    if (SpecTransPurch)
    {
        documentType = VendSpecTransOverviewFormMode::PurchaseOrder;
    }
    else if (SpecTransPaymProposal)
    {
        documentType = VendSpecTransOverviewFormMode::PaymentProposal;
    }
    else
    {
        documentType = VendSpecTransOverviewFormMode::Journal;
    }

    return documentType;
}

private void changeDocumentSelection(VendSpecTransOverviewFormMode _newSelection)
{
    if (_newSelection != lastSelection)
    {
        ViewComboBox.selection(_newSelection);

        this.selectActiveTab(_newSelection);
    }
}

private void selectActiveTab(VendSpecTransOverviewFormMode _newSelection)
{
    switch (_newSelection)
    {
        case VendSpecTransOverviewFormMode::Journal:
                tmpCustVendTrans.setTmpData(
                    CustVendFindSettlements::findMarkedTrans(
                        ModuleCustVend::Vend,
                        ledgerJournalTrans.company(),
                        ledgerJournalTrans.TableId,
                        ledgerJournalTrans.RecId));
            break;

        case VendSpecTransOverviewFormMode::PurchaseOrder:
                tmpCustVendTrans.setTmpData(
                    CustVendFindSettlements::findMarkedTrans(
                        ModuleCustVend::Vend,
                        purchTable.company(),
                        purchTable.TableId,
                        purchTable.RecId));
            break;

        case VendSpecTransOverviewFormMode::PaymentProposal:
                tmpCustVendTrans.setTmpData(
                    CustVendFindSettlements::findMarkedTrans(
                        ModuleCustVend::Vend,
                        custVendPaymProposalLine.company(),
                        custVendPaymProposalLine.TableId,
                        custVendPaymProposalLine.RecId));
            break;
    }

    // Hide the last active tab control
    FormTabControl tabControl = mapSelection2TabControl.lookup(lastSelection);
    tabControl.visible(false);

    // Reveal the new active tab control
    tabControl = mapSelection2TabControl.lookup(_newSelection);
    tabControl.visible(true);

    lastSelection = _newSelection;
}

public int active()
{
    int ret;

    ret = super();

    tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(
        ModuleCustVend::Vend,
        ledgerJournalTrans.company(),
        ledgerJournalTrans.TableId,
        ledgerJournalTrans.RecId));
    tmpCustVendTrans_DS.research();

    LedgerJournalTrans_AccountNum.parmDataAreaId(ledgerJournalTrans.Company);
    LedgerJournalTrans_AccountNum1.parmDataAreaId(ledgerJournalTrans.Company);
    LedgerJournalTrans_OffsetAccount1.parmDataAreaId(ledgerJournalTrans.getOffsetCompany());

    return ret;
}

public void jumpRef()
{
    Args args = new Args();
    args.record(ledgerJournalTrans);

    new MenuFunction(menuitemDisplayStr(VendBankAccounts), MenuItemType::Display).run(args);
}

public void modified()
{
    super();
    LedgerJournalTrans_OffsetAccount1.parmDataAreaId(ledgerJournalTrans.getOffsetCompany());
}

public int active()
{
    int ret;

    ret = super();

    tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(ModuleCustVend::Vend, purchTable.company(), purchTable.TableId, purchTable.RecId));
    tmpCustVendTrans_DS.research();

