public void init()
{
    super();

    this.query().dataSourceTable(tableNum(PurchTable)).addLink(fieldNum(SpecTrans, SpecTableId), fieldNum(PurchTable, TableId));
    this.query().dataSourceTable(tableNum(PurchTable)).addLink(fieldNum(SpecTrans, SpecRecId), fieldNum(PurchTable, RecId));

    this.cacheAddMethod(tableMethodStr(PurchTable, editContactPersonName));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastInvoice));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastInvoiceDate));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastPackingSlip));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastPackingSlipDate));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastPurchaseOrder));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastPurchaseOrderDate));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastReceiptsList));
    this.cacheAddMethod(tableMethodStr(PurchTable, lastReceiptsListDate));
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(CustVendPaymProposalLine, accumulatedCashDiscAmountMST));
    this.cacheAddMethod(tableMethodStr(CustVendPaymProposalLine, paymAmountCur));
    this.cacheAddMethod(tableMethodStr(CustVendPaymProposalLine, paymAmountMST));
}

