public boolean modified()
{
    boolean ret = super();

    element.selectActiveTab(this.selection());

    tmpCustVendTrans_DS.research();

    return ret;
}

void clicked()
{
    MenuFunction menuFunction;
    Args args = new Args();
    str menuItemStr = LedgerJournalStatic::menuItemNameForJournalType(ledgerJournalTable);

    super();

    args.record(ledgerJournalTable);
    args.parmEnumType(enumNum(LedgerJournalType));
    args.parmEnum(enum2int(ledgerJournalTable.JournalType));

    menuFunction = new MenuFunction(menuItemStr, MenuItemType::Display);
    menuFunction.run(args);
}

public void clicked()
{
    if (ledgerJournalTrans.AccountType != LedgerJournalACType::Ledger)
    {
        accountDimension.enabled(true);
    }
    else
    {
        accountDimension.enabled(false);
    }

    if (ledgerJournalTrans.OffsetAccountType != LedgerJournalACType::Ledger &&
                ledgerJournalTrans.OffsetLedgerDimension != 0)
    {
        offsetAccountDimension.enabled(true);
    }
    else
    {
        offsetAccountDimension.enabled(false);
    }

    super();
}

public void clicked()
{
    FormRun formRun;
    MenuFunction menuFunction;
    Args args;

    // Launch the Dimension form modally, this form will change the default dimension field on the record
    menuFunction = new MenuFunction(menuitemDisplayStr(LedgerJournalTransDimension), MenuItemType::Display);
    args = new Args();
    args.record(ledgerJournalTrans);
    formRun = menuFunction.create(args);
    formRun.run();
    formRun.wait(true);
}

