/// <summary>
/// Opens the form to create a vendor.
/// </summary>
[Replaceable]
protected void createVendor()
{
    partyForm.initValue();
    partyForm.create();
}

public void createRecord(str _formDataSourceName, boolean _append = false)
{
    if (conFind(mainDatasources, this.dataSource(_formDataSourceName).id()))
    {
        this.createVendor();
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

/// <summary>
///    Disables and enables field controls for when this form is called from <c>InventTrackingDimTracing</c> form.
/// </summary>
/// <remarks>
///    This is specific to tracing and will not be used in the normal calling path.
/// </remarks>
private void disableEnableControlsForItemTracing()
{
    VendTable_Currency.visible(false);
}

public void enableRfcCurpFields_MX(VendTable _vendTable)
{
    boolean isMexicanEntityOrPerson = (_vendTable.CompanyType_MX == CompanyType_MX::LegalEntity) || (_vendTable.CompanyType_MX == CompanyType_MX::LegalPerson);

    vendTable_ds.object(fieldNum(VendTable, Curp_MX)).enabled(isMexicanEntityOrPerson);
    vendTable_ds.object(fieldNum(VendTable, StateInscription_MX)).enabled(isMexicanEntityOrPerson);
}

void enableVATTaxAgent_RU()
{
    vatPartnerKind_RU.allowEdit(vendTable.vatTaxAgent_RU);
    vatOperationCode_RU.allowEdit(vendTable.vatTaxAgent_RU);
}

public void enableVendor1099OIDFields_US(Vend1099OIDDetail _vend1099OIDDetail)
{
    vend1099OIDDetail_ds.object(fieldNum(Vend1099OIDDetail, cusipId)).enabled(_vend1099OIDDetail.cusip);
    vend1099OIDDetail_ds.object(fieldNum(Vend1099OIDDetail, cusipDetails)).enabled(!_vend1099OIDDetail.cusip);
}

public void enableVendorTypeFields_MX(VendTable _vendTable)
{
    boolean isForeignVendor;

    isForeignVendor = _vendTable.VendorType_MX == VendorType_MX::ForeignVendor;

    vendTable_ds.object(fieldNum(VendTable, ForeignTaxRegistration_MX)).enabled(isForeignVendor);
    vendTable_ds.object(fieldNum(VendTable, DiotCountryCode_MX)).enabled(isForeignVendor);
    vendTable_ds.object(fieldNum(VendTable, Nationality_MX)).enabled(isForeignVendor);
}

